/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rbs3020-service:searchSupplReq" ) {

	}

	// 저장 완료 후
	if ( sServiceName == "rbs3020-service:saveSupplReq" ) {

		fcSendMngMsg();

		fcSearch();		
	}	

	// 저장 완료 후
	if ( sServiceName == "rbs3020-service:saveSupplReqSign" ) {

		fcSearch();		
	}	



		// 저장 완료 후
	if ( sServiceName == "rbs3040-service:saveSupplRegi" ) {

		fcSearch();	
	}	

	if (sServiceName == "rbs3020-service:searchNotAprv") {
		if (dsNotAprv.RowCount()>0) {
			if (toNumber(dsNotAprv.GetColumn(0, "NOT_APRV_CNT")) > 0) {
				fnc_message("소모품 수령 확인을 하지 않은 자료가 존재합니다.\n"+
				            "소모품 신청전에 기존에 신청했던 자료에 대한 수령확인을 하시기 바랍니다.","N");
			}
		}
	}
}

