/*-------------------------------------------------------------------------------------------------+
>>  Column의 값이 변경된 경우
+-------------------------------------------------------------------------------------------------*/


/*-------------------------------------------------------------------------------------------------+
>>  Dataset의 row가 변경된 경우
+-------------------------------------------------------------------------------------------------*/
function fcDsOnRowPosChanged(obj,nOldRow,nRow)
{
	//승인여부가 '신청'인 경우에만 '신청내용'을 변경할 수 있도록 한다.	

	if( obj.GetColumn(nRow, "PROG_STAT_CD") == "001" || obj.getRowType(nRow) =="insert" ) {	//승인여부가 '신청'
		taReqCntnt.Readonly = false;

		taReqCntnt.SetFocus(); // 입력 첫 항목으로 Focus

		dsSupplRegi.ClearData();

	} else {
		taReqCntnt.Readonly = true;


		fcRegiSearch();	//신청품목 조회
	}
}
