/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	//Return Key Press時 Next Component focus
	if ( nChar == ENTER_KEY ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}
	//Control Key가 눌려져 있다면 단축키 처리
	//if (bControl) fnc_HotKey(this, nChar);
}



function fcSendMngMsg(){

	  var sSendUserId   = gdsUser.GetColumn(0,"USER_ID");
	  var sSendUserNm   = gdsUser.GetColumn(0,"USER_NM");
	  var sSendUserHpNo = gdsUser.GetColumn(0,"HP_NO");

	  var sRecvUser = "";
	  var sTitle	= "소모품신청";
	  var sDesc		= "[소모품신청]\n"+dsSupplReq.GetColumn(dsSupplReq.row,"REQ_CNTNT") ;

	  var AlarmCd = "";
	  if      (rdoGbn.Value == "002") 	AlarmCd = "012";	//경륜토너관리자
	  else if (rdoGbn.Value == "003") 	AlarmCd = "013";	//경정소모품관리자
	  else 								AlarmCd = "002";	//경륜 소모품 관리자

	  fcd_SendMsgByAlarmDs(AlarmCd, sSendUserId, sSendUserNm, sSendUserHpNo, sTitle, sDesc, "RBS3030" );
}



function fcEdOnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
    switch ( obj.id ) {
		case "edReqCntntSearch" :
			if ( nChar == ENTER_KEY) {		
				fcSearch();		
			}
			break;

	}

}
// 지점입고 or 지점입고 취소
function fcBtnOnClick(obj)
{

	var nChkCnt = 0;

	// 지점입고
	if( obj.Id == "Signbtn" ) {

		for(var i=0; i < dsSupplRegi.rowcount; i++) {
			if( dsSupplRegi.GetColumn(i, "CHK") == "1" ) {
				//승인상태가 '003'이 아니라면(승인완료) 

				nChkCnt++;
			}
		}

		if( nChkCnt == 0 ) {
			//	[ @ ] 처리할 데이터가 없습니다.
			fnc_Message(SNIS_RBM_B017, "N", "수령");
			return;
		}

		//	[ @ ]하시겠습니까?
		if(fnc_Message(SNIS_RBM_E021, "Y")) {

			fcSignSave();	
		    /*
			DivBrncNo.Visible = true;		// 서명
			DivBrncNo.edtUserId.SetFocus();
			*/
		}		


	} 
}




