	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rbs3050-service:searchOrderHist";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOrderHist=dsOrderHist";  

    //조회조건
	fcd_AddParam("PRHS_DT_FROM", calFromDt.Value);
	fcd_AddParam("PRHS_DT_TO"  , calToDt.Value);
	fcd_AddParam("BIZ_GBN"     , rdoGbn.Value);
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

// 저장 전에 체크
function fcSaveCheck(obj) {  
	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsSupplHistPop) && !fnc_DatasetChangeCheck(dsOrderHist) ) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	// 필수항목체크
	oDataSet          = dsSupplHistPop;
	sMandatoryColID   = "SUPPL_CD|QTY";
	sMandatoryColName = "소모품|수량";
	oGrid             = grdSupplHist;

	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	var sPrdsDt = dsOrderHist.GetColumn(dsOrderHist.row, "PRHS_DT");
	for (icnt=0;icnt<dsSupplHistPop.rowcount();icnt++) {
		if (dsSupplHistPop.GetColumn(icnt, "PRHS_DT") <> sPrdsDt) {
			dsSupplHistPop.SetColumn(icnt, "PRHS_DT", sPrdsDt);
		}
	}		

	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck() ) return;   

    if(fnc_Message(SNIS_COM_1012, "Y")) {           
		var sServiceName = "rbs3050-service:saveSupplOrder";
		var sInDataSet   = "dsSupplHist=dsSupplHistPop:u  dsSupplStk=dsSupplStkPop:u ";
		var sOutDataSet  = "dsOrderHist=dsOrderHist ";  	

		//조회조건
		fcd_AddParam("SUPPL_CD"    , "");
		fcd_AddParam("PRHS_DT_FROM", calFromDt.Value);
		fcd_AddParam("PRHS_DT_TO"  , calToDt.Value);
		fcd_AddParam("BIZ_GBN"     , rdoGbn.Value);

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
    }
}

