/***************************************************************************************************
*                                      Grid 삽입, 추가, 삭제 처리                                  *
***************************************************************************************************/
function fcAddRow(obj) {
	var nRow = -1;
	switch (obj.GetForm().id) {
		case "SuppBtnRow":
			if (dsOrderHist.FindRow("PRHS_DT", sCurDate) >= 0) {
				fnc_message(SNIS_RBM_E025, "N", sCurDate);
				return;
			}

			nRow = dsOrderHist.AddRow();

			//자동 Setting
			dsOrderHist.SetColumn(nRow, "PRHS_DT",  sCurDate);
			dsOrderHist.SetColumn(nRow, "BIZ_GBN",  rdoGbn.Value);

			dsSupplHistPop.SetCellPos(2);
			dsSupplHistPop.SetFocus(); // 입력 첫 항목으로 Focus
			break;

	}
}


/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	 switch (obj.GetForm().id) {
		 case "SuppBtnRow":
			if ( !fnc_DeleteCheck(dsOrderHist, "chk", "1") ) return;
			break;			
	 }	
}

/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddSelect(obj) {
	if (dsOrderHist.rowcount() ==0) return;

	var nRow = -1;
	var sRow = -1;
	var tRow = -1;
	var sSupplCd = "";
	for (nRow=0;nRow<dsSupplStkPop.rowcount();nRow++) {
		if (dsSupplStkPop.GetColumn(nRow, "CHK") == "1") {
			sSupplCd = dsSupplStkPop.GetColumn(nRow, "SUPPL_CD");
			sRow = dsSupplHistPop.FindRow("SUPPL_CD", sSupplCd);
			if (sRow == -1) {	// 기존에 없는 경우에만 추가
				tRow = dsSupplHistPop.AddRow();
				dsSupplHistPop.SetColumn(tRow, "SUPPL_CD", sSupplCd);
				dsSupplHistPop.SetColumn(tRow, "SUPPL_CD_NM", dsSupplStkPop.GetColumn(nRow, "SUPPL_CD_NM"));
				dsSupplHistPop.SetColumn(tRow, "MANUF_NM", 	  dsSupplStkPop.GetColumn(nRow, "MANUF_NM"));
				dsSupplHistPop.SetColumn(tRow, "PRHS_DT",  sCurDate);
				dsSupplHistPop.SetColumn(tRow, "BIZ_GBN",  rdoGbn.Value);
				dsSupplHistPop.SetColumn(tRow, "QTY",  "0");
			}
			dsSupplStkPop.SetColumn(nRow, "CHK", "0");			
		}
	}
	//dsSupplHistPop.SetCellPos(2);
	//dsSupplHistPop.SetFocus(); // 입력 첫 항목으로 Focus
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelSelect(obj) {
	for(var icnt=dsSupplHistPop.rowcount() -1;icnt>=0;icnt--) {
		if (dsSupplHistPop.GetColumn(icnt, "chk") == "1") {
			dsSupplHistPop.DeleteRow(icnt);
		}
	}
}


