/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	if (sServiceName == "common-service:searchCode") {
		//TrACE(dsConsumType.savexml());
	} else if ( sServiceName == "rbs3050-service:searchOrderHist") {

	} else if ( sServiceName == "rbs3050-service:saveSupplOrder" ) {
		fcSearchStcks();
	} else if (sServiceName == "rbs3050-service:GatherRequest"	) {
		var nRow=0;
		for (iCnt=0;iCnt<dsSupplHistPopNew.RowCount();iCnt++) {
			nRow = dsSupplHistPop.AddRow();
			dsSupplHistPop.SetColumn(nRow, "PRHS_DT", 	dsOrderHist.GetColumn(dsOrderHist.row, "PRHS_DT"));
			dsSupplHistPop.SetColumn(nRow, "SUPPL_CD", 	dsSupplHistPopNew.GetColumn(iCnt, "SUPPL_CD"));
			dsSupplHistPop.SetColumn(nRow, "MANUF_NM", 	dsSupplHistPopNew.GetColumn(iCnt, "MANUF_NM"));
			dsSupplHistPop.SetColumn(nRow, "SUPPL_CD_NM", 	dsSupplHistPopNew.GetColumn(iCnt, "SUPPL_CD_NM"));
			dsSupplHistPop.SetColumn(nRow, "QTY", 		dsSupplHistPopNew.GetColumn(iCnt, "QTY"));
		}

	}
}

