/*-------------------------------------------------------------------------------------------------+
>>  Column의 값이 변경된 경우
+-------------------------------------------------------------------------------------------------*/
function fcDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	switch (obj.id) {
		case "dsSupplHist" :	//소모품이력
			if( strColumnID == "QTY") {
				if( varNewValue < 1 ) {
					fnc_Message(SNIS_RBM_E006, "N", nRow+1, "수량");
					dsSupplHist.SetColumn(nRow, strColumnID, varOldValue);
				}
			}
			break;
	}
}

