/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	//Return Key Press時 Next Component focus
	if ( nChar == ENTER_KEY ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}
	//Control Key가 눌려져 있다면 단축키 처리
	//if (bControl) fnc_HotKey(this, nChar);
}
function fcDsOnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	if( nCell == 1) {
		fnc_SetPopupDivCalendar(obj, nRow, nCell);		
	}
}



function fcsetManufCol()
{
	if (rdoGbn.Value == "002") {
		grdSupplStk.SetColProp(1, "Width", 70);
	} else {
		grdSupplStk.SetColProp(1, "Width", 0);
	}
}

function dsOrderHist_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	fcSearchOrderConts();	
}
function fcSearchOrderConts()
{
	if (dsOrderHist.rowcount() < 1) return;
	var nRow = dsOrderHist.row;

	var sServiceName = "rbs3050-service:searchOrderConts";
    var sInDataSet   = "";
    var sOutDataSet  = "dsSupplHistPop=dsSupplHistPop";  

    //조회조건
	fcd_AddParam("PRHS_DT", dsOrderHist.GetColumn(nRow, "PRHS_DT"));
	fcd_AddParam("BIZ_GBN", rdoGbn.Value);
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}

function dsOrderHist_OnRowPosChanged(obj,nOldRow,nRow)
{
	fcSearchOrderConts();	
}

function fcSearchStcks()
{
	var sServiceName = "rbs3010-service:searchSupplStk";
    var sInDataSet   = "";
    var sOutDataSet  = "dsSupplStkPop=dsSupplStk";  

    //조회조건
	fcd_AddParam("SUPPL_CD", "");
	fcd_AddParam("BIZ_GBN" , rdoGbn.Value);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}


function fcGatherRequest(obj)
{
	var nRow = 0;
	if (dsSupplHistPop.RowCount() > 0) {
		fnc_message("기존 입력된 자료를 삭제후 계속 진행하세요.","N");
		return;
	}
	if (dsOrderHist.rowcount() == 0) {
		nRow = dsOrderHist.AddRow();
		dsOrderHist.SetColumn(nRow, "PRHS_DT", sCurDate);
		dsOrderHist.SetColumn(nRow, "PRHS_DT", sCurDate);
	}

	var sServiceName = "rbs3050-service:GatherRequest";
    var sInDataSet   = "";
    var sOutDataSet  = "dsSupplHistPopNew=dsSupplHistPop";  

    //조회조건
	fcd_AddParam("PRHS_DT_FROM", calFromDt.Value);
	fcd_AddParam("PRHS_DT_TO"  , calToDt.Value);
	fcd_AddParam("BIZ_GBN",      rdoGbn.Value);
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}
