/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {


	dsReportSubm.ClearData();	//보고서 

	dsReportUploadFile.ClearData();	//첨부파일

	dsSubmUploadFile.ClearData();	//양식담당자 

}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}



/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";
	if ( !fcSearchCheck(obj) ) return;


	 sServiceName = "rbs4020-service:searchReportSubm";
	 sInDataSet   = "";
	 sOutDataSet  = "dsReportSubm=dsReportSubm ";

	 //조회조건 : 
	 fcd_AddParam("DEPT_CD", sDeptCd);
	 fcd_AddParam("SUBM_LIMIT_STR_DT", calSubmLimitStrDt.Value);
	 fcd_AddParam("SUBM_LIMIT_END_DT", calSubmLimitEndDt.Value);
	 fcd_AddParam("REPORT_NM", edSearchReportNm.Text);

	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}




/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsReportSubm) && !fnc_DatasetChangeCheck(dsSubmUploadFile)) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	if(dsReportSubm.GetColumn(dsReportSubm.row,"REPORT_CONT") == null ||dsReportSubm.GetColumn(dsReportSubm.row,"REPORT_CONT")=="" ){
			fnc_Message(SNIS_COM_1035, "N","보고내용"); 
			taReportCont.SetFocus();
			return false;
	}


	// 보고서 필수항목체크
	oDataSet          = dsReportSubm;
	sMandatoryColID   = "REPORT_CONT";
	sMandatoryColName = "보고내용";
	oGrid             =  grdReportSubm;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";


	switch ( obj.id ) {
		case "btnSave":
			if ( !fcSaveCheck() ) return;


			sServiceName = "rbs4020-service:saveReportSubm";
			sInDataSet   = "dsReportSubm=dsReportSubm:u dsSubmUploadFile=dsSubmUploadFile:u";

			sOutDataSet  = "dsReportSubm=dsReportSubm ";

			//첨부파일 서버 업로드 
			if( dsSubmUploadFile.GetRowCount() > 0 ) {
				for ( i = 0; i < dsSubmUploadFile.GetRowCount(); i++ ) {
					if(dsSubmUploadFile.getColumn(i, "CHK") == "1" ) {
						var rtn_arr = fnc_FileWrite(  divBtnLst.HttpFile 
													, dsSubmUploadFile.GetColumn(i, "FILE_PATH" )
													, dsSubmUploadFile.GetColumn(i, "FILE_NM" ) 
													,"");

						var fileSize = divBtnLst.HttpFile.GetFileSizeLocal(dsSubmUploadFile.GetColumn(i, "FILE_PATH" ));

						if ( rtn_arr[0] != "SUCC" ) {
							alert(rtn_arr[1] + "파일 업로드 에러발생");
							return false;
						}


						dsSubmUploadFile.SetColumn(i, "FILE_PATH"    , rtn_arr[1] );
						dsSubmUploadFile.SetColumn(i, "FILE_SIZE"   , fileSize );

					} // end if
				}
			} // end 


		fcd_AddParam("DEPT_CD", dsReportSubm.GetColumn(dsReportSubm.row,"DEPT_CD"));
		fcd_AddParam("STND_YEAR", dsReportSubm.GetColumn(dsReportSubm.row,"STND_YEAR"));
		fcd_AddParam("SEQ", dsReportSubm.GetColumn(dsReportSubm.row,"SEQ"));
		fcd_AddParam("MNG_ID", dsReportSubm.GetColumn(dsReportSubm.row,"MNG_ID"));


		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

		break;

	}


}



/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	// DataSet의 Header 정보가 있을 때  Add 처리
	switch ( obj.GetForm().id ) {

	    case "fileBtnRow" : //양식담당자 그리드 행추가,삭제 
	    	if ( dsReportSubm.GetRowCount() < 0) {

				return;
			}

			sResponse = fcd_GetSelectFile();	
			row = dsSubmUploadFile.addRow();

			dsSubmUploadFile.SetColumn(row, "ATT_FILE_KEY" , dsReportSubm.GetColumn(dsReportSubm.currow,"ATT_FILE_KEY"));


			if ( !fnc_IsNull(sResponse) ) {
				dsSubmUploadFile.SetColumn(row, "CHK"       , "1"         );
				dsSubmUploadFile.SetColumn(row, "FILE_PATH"  , sResponse[0] + "\\" + sResponse[1]);
				dsSubmUploadFile.SetColumn(row, "FILE_NM" , sResponse[1]);


			}	

			if ( dsSubmUploadFile.getColumn(dsSubmUploadFile.row, "CHK") != "1") {
				dsSubmUploadFile.rowcount = dsSubmUploadFile.DeleteRow(row);
			}

			break;


	}
}



/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	// DataSet의 Header 정보가 있을 때  Add 처리
	switch ( obj.GetForm().id ) {	
		case "fileBtnRow" : //파일담당자 그리드 행추가,삭제 
			if ( !fnc_DeleteCheck(dsSubmUploadFile, "chk", "1") ) return;

			break;	

	}
}



