/*-------------------------------------------------------------------------------------------------+
>>  dataset  OnRowPosChanged event 처리 
+-------------------------------------------------------------------------------------------------*/
function fcDsOnRowPosChanged(obj,nOldRow,nRow)
{
	 switch ( obj.id ) {
		//장비 목록 
		case "dsReportSubm" :	// 보고서제출
			if(nRow>-1){
					taReportCont.SetFocus();
					fcDtlSearch();
			}

			break;
	 }

}


