/*-------------------------------------------------------------------------------------------------+
>>  Grid   OnCellDBLClick  event 처리 
+-------------------------------------------------------------------------------------------------*/
function fcGrdOnCellDBLClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	switch ( obj.id ) {
		//보고서양식 선택 
		case "grdReportUploadFile" :

				var sLocalFileName  = fcd_GetSelectFile("Save", dsReportUploadFile.GetColumn(nRow, "FILE_NM"));
				var sServerFileName = dsReportUploadFile.GetColumn(nRow, "FILE_PATH");
				var sFileSize       = dsReportUploadFile.GetColumn(nRow, "FILE_SIZE");
				if ( sLocalFileName == null ) return;

				fnc_FileRead(divBtnLst.HttpFile, sLocalFileName[0] + "\\" + sLocalFileName[1], sServerFileName, sFileSize, nRow, "PROG_BAR", dsReportUploadFile);

			break;

		//보고서제출첨부파일 선택 
		case "grdSubmUploadFile" :

				var sLocalFileName  = fcd_GetSelectFile("Save", dsSubmUploadFile.GetColumn(nRow, "FILE_NM"));
				var sServerFileName = dsSubmUploadFile.GetColumn(nRow, "FILE_PATH");
				var sFileSize       = dsSubmUploadFile.GetColumn(nRow, "FILE_SIZE");
				if ( sLocalFileName == null ) return;

				fnc_FileRead(divBtnLst.HttpFile, sLocalFileName[0] + "\\" + sLocalFileName[1], sServerFileName, sFileSize, nRow, "PROG_BAR", dsSubmUploadFile);

			break;
	 }
}

