/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 보고서 조회 완료 후
	if ( sServiceName == "rbs4020-service:searchReportSubm" ) {

	}


	 // 보고서 저장 완료 후
	if ( sServiceName == "rbs4020-service:saveReportSubm" ) {

		fcSearch();
	}

	// 첨부파일 조회  완료 후
	if ( sServiceName == "rbs4020-service:searchSubmFile" ) {


	}

}


/*-------------------------------------------------------------------------------------------------+
>>  양식파일, 보고서 담당자 조회 
+-------------------------------------------------------------------------------------------------*/
function fcDtlSearch(){
	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";


	//양식 첨부파일 조회 
	 sServiceName = "rbs4020-service:searchReportFile";
	 sInDataSet   = "";
	 sOutDataSet  = "dsReportUploadFile=dsReportUploadFile ";

	 //조회조건 : 
	 fcd_AddParam("ATT_FILE_KEY", dsReportSubm.GetColumn(dsReportSubm.row, "REPORT_ATT_FILE_KEY"  ));

	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);



	 //제출  첨부파일 조회 
	 sServiceName = "rbs4020-service:searchSubmFile";
	 sInDataSet   = "";
	 sOutDataSet  = "dsSubmUploadFile=dsSubmUploadFile ";

	 //조회조건 : 
	 fcd_AddParam("ATT_FILE_KEY", dsReportSubm.GetColumn(dsReportSubm.row, "ATT_FILE_KEY"  ));

	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}


