function fcInit(obj) {
    dsWinHis.ClearData();  
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	if (!fcSearchCheck(obj)) return;// 필수조건 체크 및 그리드 초기화

	var sServiceName = "rbs5220-service:searchWinHis";  // Grid 조회
	var sInDataSet   = "";
	var sOutDataSet  = "dsWinHis=dsWinHis";     

	fcd_AddParam("GAME_CD", cboGameCd.Value);        // 종목코드  
	fcd_AddParam("STR_DY", calStrDt.Value);        // 조회시작일자
	fcd_AddParam("END_DY", calEndDt.Value);        // 조회종료일자

	if (chkRacerOnly.value == "1") {
		fcd_AddParam("RACER_ID", dsRacerList.GetColumn(dsRacerList.row, "RACER_ID"));        // 종목코드  	
	}

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}


function fcSearchRacer(obj) {

	var sServiceName = "rbs5010-service:searchRacerList";  // Grid 조회
	var sInDataSet   = "";
	var sOutDataSet  = "dsRacerList=dsRacerList";     

	fcd_AddParam("GAME_CD", cboGameCd.Value);        // 종목코드  
	fcd_AddParam("STR_DY", calStrDt.Value);        // 조회시작일자
	fcd_AddParam("END_DY", calEndDt.Value);        // 조회종료일자

	if (chkRacerOnly.value == "1") {
		fcd_AddParam("RACER_ID", dsRacerList.GetColumn(dsRacerList.row, "RACER_ID"));        // 종목코드  	
	}

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {
	if ( !fcSaveCheck() ) return;

	var sServiceName = "rbs5220-service:saveWinHis";
	var sInDataSet   = "dsWinHis=dsWinHis:u";
	var sOutDataSet  = "dsWinHis=dsWinHis";

	fcd_AddParam("GAME_CD", cboGameCd.Value);        // 종목코드  
	fcd_AddParam("STR_DY", calStrDt.Value);        // 조회시작일자
	fcd_AddParam("END_DY", calEndDt.Value);        // 조회종료일자

	if (chkRacerOnly.value == "1") {
		fcd_AddParam("RACER_ID", dsRacerList.GetColumn(dsRacerList.row, "RACER_ID"));        // 종목코드  	
	}

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

