/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
    var nRow = 0;
	if (obj.GetForm().id == "specBtnRow") {
		nRow = dsWinHis.AddRow(); // Record Add
		if (Length(cboGameCd.Value) == 3) {
			dsWinHis.SetColumn(nRow, "GAME_CD", cboGameCd.Value);
		}
		if (chkRacerOnly.value == "1") {
			if (dsRacerList.RowCount() > 0 && dsRacerList.row >= 0) {
				dsWinHis.SetColumn(nRow, "RACER_ID", dsRacerList.GetColumn(dsRacerList.Row, "RACER_ID"));
				dsWinHis.SetColumn(nRow, "RACER_NM", dsRacerList.GetColumn(dsRacerList.Row, "RACER_NM"));
			}
		}
		dsWinHis.SetColumn(nRow, "PRV_GRP_TYPE", dsRacerList.GetColumn(dsRacerList.Row, "001"));
	}		
}


