function fcInsRow(obj) {
    // 여기서는 사용하지 않는다.	
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {

	if (obj.GetForm().id == "specBtnRow") {
		var dCnt = 0;
		for(var i = dsWinHis.GetRowCount() -1; i >= 0 ; i--) {
			if(dsWinHis.GetColumn(i, "chk") == 1)
				dsWinHis.DeleteRow(i);
		}		
	}
}


/***************************************************************************************************
*                          CALL BACK 처리 부분                                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	if ( sServiceName == "common-service:searchCode" ) {
		if (fcGamefilter() == true) {	
			// 공통코드 조회 완료 후
			var nRow = dsGameCd.InsertRow(0);
			dsGameCd.SetColumn(nRow, "CD", "%");
			dsGameCd.SetColumn(nRow, "CD_NM", "전체");
		}
	}			

	if ( sServiceName == "rbs5220-service:searchWinHis" ) {

	}			


}



