/*-------------------------------------------------------------------------------------------------+
>>  대회 팝업 열기  
+-------------------------------------------------------------------------------------------------*/
function fcContestDialog(obj)
{
	var sGameCd = "";
	if (dsWinHis.rowcount() == 0) {
		fnc_message("한줄을 추가후 작업하세요.", "N");
		return;
	}
	var nRow = dsWinHis.Row;
	var args  = "pGameCd=" + quote(dsWinHis.GetColumn(nRow, "GAME_CD")) + " MULTY=" + quote("N");	
	var bRtn = Dialog("frm_rbs::RBS5030P.xml", args, 334, 443, "");

	if(bRtn) {
		dsWinHis.SetColumn(nRow, "GAME_CD", 			G_ds_return.GetColumn(0, "GAME_CD"));		
		dsWinHis.SetColumn(nRow, "GAME_NM", 			G_ds_return.GetColumn(0, "GAME_NM"));		
		dsWinHis.SetColumn(nRow, "CONTEST_TYPE_SEQ", 	G_ds_return.GetColumn(0, "CONTEST_TYPE_SEQ"));		
		dsWinHis.SetColumn(nRow, "CONTEST_TYPE_NM", 	G_ds_return.GetColumn(0, "CONTEST_TYPE_NM"));		
		dsWinHis.SetColumn(nRow, "INTL_TYPE", 			G_ds_return.GetColumn(0, "INTL_TYPE"));		
		dsWinHis.SetColumn(nRow, "CONTEST_SEQ", 		G_ds_return.GetColumn(0, "CONTEST_SEQ"));		
		dsWinHis.SetColumn(nRow, "CONTEST_NM", 			G_ds_return.GetColumn(0, "CONTEST_NM"));		
		if (fnc_IsNull(dsWinHis.GetColumn(nRow, "WIN_DY"))) {
			dsWinHis.SetColumn(nRow, "WIN_DY", 				G_ds_return.GetColumn(0, "STR_DY"));		
		}
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  선수검색 팝업 열기  
+-------------------------------------------------------------------------------------------------*/
function fcRacerDialog(obj)
{
	var sGameCd = "";
	if (dsWinHis.rowcount() == 0) {
		fnc_message("한줄을 추가후 작업하세요.", "N");
		return;
	}
	var nRow = dsWinHis.Row;
	var args = "pGameCd=" + quote(dsWinHis.GetColumn(nRow, "GAME_CD")) + " MULTY=" + quote("N");	
	var bRtn = Dialog("frm_rbs::RBS5010P.xml", args, 334, 443, "");

	if(bRtn) {
		dsWinHis.SetColumn(nRow, "GAME_CD", 	G_ds_return.GetColumn(0, "GAME_CD"));		
		dsWinHis.SetColumn(nRow, "GAME_NM", 	G_ds_return.GetColumn(0, "GAME_NM"));		
		dsWinHis.SetColumn(nRow, "RACER_ID", 	G_ds_return.GetColumn(0, "RACER_ID"));		
		dsWinHis.SetColumn(nRow, "RACER_NM", 	G_ds_return.GetColumn(0, "RACER_NM"));		
	}
}

function fcOnRowPosChanged(obj,nOldRow,nRow)
{
	if (obj.id == "dsRacerList") {
		if (chkRacerOnly.value == "1") {
			var filterStr = "RACER_ID='" + dsRacerList.GetColumn(dsRacerList.row, "RACER_ID") + "'";
			dsWinHis.Filter(filterStr);
		}	
	} else if (obj.id == "dsWinHis") {
		fcCheckVisible(nRow);
	}
}

function fcOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch (obj.id) {
		case "cboGameCd":
			setReg("GAME_CD", cboGameCd.Value);
			fcSearchRacer();
			chkRacerOnly.Value = "0";
			fcSearch();
			break;
	}
}


function fcOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (obj.id == "dsWinHis") {
		if (strColumnID == "GAME_CD") {
			fcCheckVisible(nRow);
		}
	}
}

function fcCheckVisible(nRow)
{
	if (dsWinHis.GetColumn(nRow, "GAME_CD") == "001" &&
	    dsWinHis.GetColumn(nRow, "INTL_TYPE") == "0") {
		stMrtnRaceType.Visible = true;
		cboMrtnRaceType.Visible = true;
	} else {
		stMrtnRaceType.Visible = false;
		cboMrtnRaceType.Visible = false;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  팀별로 관리하는 종목만 조회되게 권한 관리
+-------------------------------------------------------------------------------------------------*/
function fcGamefilter(obj)
{
	if (IsExistVar("sApro", true)) {
		if (sApro != "T" && sMnAuthRmk != "") {
			cboGameCd.Value = sMnAuthRmk;
			cboGameCd.Enable = false;
			//var filterStr = "CD_TERM1=" + quote(GdsUser.GetColumn(0, "TEAM_CD"));
			//dsGameCd.Filter(filterStr);
			return false;
		} else {
			cboGameCd.Value = GetReg("GAME_CD");	
		}
    }
    return true;
}
