/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {
	if ( !fcSaveCheck() ) return;

	// 관리자 저장시 접수
	if (bAdmin == true && dsSuprtList.GetColumn(dsSuprtList.row, "STAT_CD") == "004") {
		dsSuprtList.SetColumn(dsSuprtList.row, "STAT_CD", "011");
	}
	// 요청자 저장시 결과입력
	if (dsSuprtList.GetColumn(dsSuprtList.row, "REQ_ID") == gdsUser.GetColumn(0, "USER_ID") && 
	    dsSuprtList.GetColumn(dsSuprtList.row, "STAT_CD") == "013") {
		dsSuprtList.SetColumn(dsSuprtList.row, "STAT_CD", "021");
	}

	var sServiceName = "rbs6020-service:saveSuprtInfo";
	var sInDataSet   = "dsSuprtList=dsSuprtList:u";
	var sOutDataSet  = "dsDupList=dsDupList ";	

	fcd_AddParam("STR_DT", substr(grid_calendar.UserData,0,6)+"01");
	fcd_AddParam("END_DT", substr(grid_calendar.UserData,0,6)+"31");
	fcd_AddParam("REQ_TEAM_CD", null);	
	fcd_AddParam("STAT_CD", null);	
	fcd_AddParam("SUPTR_ID", null);	
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}
/*-------------------------------------------------------------------------------------------------+
>>  결재요청/ 회수 등 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveStat(curStatCd, suprtStatCd) {
	// 상태 체크 필요한가? : 일단 안함
	if (suprtStatCd == "100" || suprtStatCd == "110" || suprtStatCd == "120") { //취소요청, 취소처리, 취소요청반려
		if (curStatCd <= "004") fnc_message("관리자 접수 전에는 취소요청을 할 수 없습니다.", "N");
		var CancelRsn = dsSuprtList.GetColumn(dsSuprtList.row, "CNCL_RSN");
		if (fnc_IsNull(CancelRsn) == true) {
			divReason.visible = true;
			return;
		}
		fcd_AddParam("CNCL_STAT_CD", suprtStatCd);		//변경할 상태
		fcd_AddParam("CNCL_RSN", CancelRsn);			//취소사유
	}	

	var sServiceName = "rbs6020-service:saveSuprtStat";
	var sInDataSet   = "";
	var sOutDataSet  = "dsDupList=dsDupList ";

	fcd_AddParam("TEAM_CD",  gdsUser.GetColumn(0, "TEAM_CD"));
	fcd_AddParam("SEQ_NO",  dsSuprtList.GetColumn(dsSuprtList.row, "SEQ_NO"));
	fcd_AddParam("CUR_STAT_CD", curStatCd);		//현재 상태
	fcd_AddParam("STAT_CD", suprtStatCd);		//변경할 상태
	fcd_AddParam("STR_DT", substr(grid_calendar.UserData,0,6)+"01");
	fcd_AddParam("END_DT", substr(grid_calendar.UserData,0,6)+"31");
	fcd_AddParam("SUPTR_ID", null);	

	slastStatCd = curStatCd+suprtStatCd;	
	slastSeqNo = dsSuprtList.GetColumn(dsSuprtList.row, "SEQ_NO");

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

