/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {
	sBUTTONLIST   = 'FTTFTFT';     // 초기화,조회,저장,삭제,엑셀,인쇄,닫기

	// 화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	// 화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//공통코드 조회
	fcd_GetComboDs(dsInComCd);

	// 년도 설정
	fcSetYear();

	cboYear.SetFocus();

	fcSearch(); // 그룹코드 조회
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	// 자료 변경 여부를 체크하고자 하는 DataSet Object를 넘긴다.
	var sDataset = "dsListYy";
	if (   !fnc_FormUnloadCheck(sDataset  , sFormId) ) return false;
}

/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	// Return Key Press時 Next Component focus
	if ( nChar == ENTER_KEY ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}

	// Control Key가 눌려져 있다면 단축키 처리
	// if (bControl) fnc_HotKey(this, nChar);
}

/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
	dsListYy.ClearData();
	dsListMm.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";
	if ( !fcSearchCheck(obj) ) return;

	if (tab0.TabIndex == 0) {
		sServiceName = "rbs9220-service:searchListMm";
		sOutDataSet  = "dsListMm=dsListMm";

		//조회조건 : 
		fcd_AddParam("STND_YEAR",	cboYear.Value);
		fcd_AddParam("STND_MM", 	cboMm.Value);
		fcd_AddParam("BRNC_CD", 	cboSearchBrncCd.Value);

	} else if (tab0.TabIndex == 1) {
		sServiceName = "rbs9220-service:searchListYy";
		sOutDataSet  = "dsListYy=dsListYy";

		//조회조건 : 
		fcd_AddParam("STND_YEAR",	cboYear.Value);
		fcd_AddParam("BRNC_CD", 	cboSearchBrncCd.Value);

	}

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsListMm)) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
		}


	// 필수항목체크
	oDataSet          = dsListMm;
	sMandatoryColID   = "STND_YEAR|STND_MM|BRNC_CD";
	sMandatoryColName = "년도|월|지점코드";
	oGrid             = Tab0.tab1.grdListMm;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {
	if ( !fcSaveCheck() ) return;

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	sServiceName = "rbs9220-service:saveList";
	sInDataSet   = "dsListMm=dsListMm:u ";
	sOutDataSet  = "dsListMm=dsListMm ";

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {

	var sExcelList = "tab0.tab1.grdListMm:월별 전자카드 발급수";
	if (tab0.TabIndex == 1) {
		sExcelList = "tab0.tab2.grdListYy:연도별 전자카드 발급수";
	}

	fcd_OpenExcelListExport(sExcelList);
}


