	dsListMm.AddRow(); // Record Add

	Tab0.tab1.grdListMm.SetCellPos(1);
	Tab0.tab1.grdListMm.SetFocus(); // 입력 첫 항목으로 Focus
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	if ( !fnc_DeleteCheck(dsListMm, "chk", "1") ) return;
}

