/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 그룹코드조회 완료 후
	if ( sServiceName == "rbs9220-service:searchListMm" ) {

	}

	if ( sServiceName == "rbs9220-service:searchListYy" ) {
		if(dsListYy.GetRowCount() > 0){
			Tab0.tab2.grdListYy.SetCellProp("head", 1, "text", dsListYy.GetColumn(0, "STND_YEAR"));
			Tab0.tab2.grdListYy.SetCellProp("head", 2, "text", dsListYy.GetColumn(0, "LAST_STND_YEAR"));
		}else{
			Tab0.tab2.grdListYy.SetCellProp("head", 1, "text", "년도");
			Tab0.tab2.grdListYy.SetCellProp("head", 2, "text", "년도");
		}

	}

	if ( sServiceName == "rbs9220-service:saveList" ) {
		fcSearch();
	}

}

function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	switch ( obj.id ) {

		case "dsBrncCd":

			dsBrncCd.InsertRow(0);
			dsBrncCd.SetColumn(0, "CD_NM", "전체");
			cboSearchBrncCd.index = 0;

			break;	

		case "dsBrncCd_0" :

			break;

	}
}

/*-------------------------------------------------------------------------------------------------+
>>  활동년도 설정 2011 년~  
+-------------------------------------------------------------------------------------------------*/
function fcSetYear(){

	// 년도 셋팅
	var sCurYear  = ParseInt(fcd_GetSysdate("YYYY"));

	for(var i=sCurYear; i >= sCurYear - 5; i--){
		var nRow = dsYear.AddRow();

		dsYear.SetColumn(nRow,"CD"   ,i);
		dsYear.SetColumn(nRow,"CD_NM",i);	
	}
}

/*-------------------------------------------------------------------------------------------------+
>> Enter Key 이벤트시 조회되도록 한다.
+-------------------------------------------------------------------------------------------------*/
function fcEdOnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if ( nChar == ENTER_KEY ) {		
		fcSearch();				
	}
}


function Tab0_OnChanged(obj,nOldIndex,nNewindex)
{
	if(tab0.TabIndex == 1){
		grpBtnRow.Visible = false;
		Static0.Visible = false;
		cboMm.Visible = false;
	}else{
		grpBtnRow.Visible = true;
		Static0.Visible = true;
		cboMm.Visible = true;	
	}
	fcSearch();	
}


function combo_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearch();	
}


function dsListMm_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	var yearBrnc = "";
	var curYearBrnc = "";

	if ( dsListMm.GetRowType(nRow) == "insert" ) {
		curYearBrnc = "|"+dsListMm.GetColumn(dsListMm.currow, "STND_YEAR")+":"+dsListMm.GetColumn(dsListMm.currow, "STND_MM")+":"+dsListMm.GetColumn(dsListMm.currow, "BRNC_CD")+"|";

		for(var i=0; i<dsListMm.rowcount; i++){
			if(i==dsListMm.currow) continue;
			yearBrnc += "|"+dsListMm.GetColumn(i, "STND_YEAR")+":"+dsListMm.GetColumn(i, "STND_MM")+":"+dsListMm.GetColumn(i, "BRNC_CD")+"|";
		}

		if(IndexOf(yearBrnc, curYearBrnc)!=-1){
			alert("중복된 지점코드가 있습니다. \n 다시 확인해 주세요.");
			dsListMm.SetColumn(dsListMm.currow, "BRNC_CD", "");
		}
	}
}
