/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
	dsList.ClearData();
	dsListAmt.ClearData();
	dsListCnt.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";
	if ( !fcSearchCheck(obj) ) return;

	sServiceName = "rbs9230-service:searchList";
	sOutDataSet  = "dsList=dsList";

	//조회조건 : 
	fcd_AddParam("CAL_FROM",	calReqStrDt.Value);
	fcd_AddParam("CAL_TO",		calReqEndDt.Value);
	fcd_AddParam("GUBUN", 		cboGubun.Value);
	if(cboSearchBrncCd.Value = '00'){
		fcd_AddParam("BRNC_CD", 	"");
	}else{
		fcd_AddParam("BRNC_CD", 	cboSearchBrncCd.Value);
	}	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


/*-------------------------------------------------------------------------------------------------+
>>  상세조회 
+-------------------------------------------------------------------------------------------------*/
function grdList_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	if(dsList.GetColumn(nRow, "COMM_NO") != '999'){		
		if (tab0.TabIndex == 0) {	

			if(dsList.GetColumn(nRow, "COMM_NO") == '01'){
				sServiceName = "rbs9230-service:searchListAmtBonjang";
				sOutDataSet  = "dsListAmt=dsListAmt";
			}else{
				sServiceName = "rbs9230-service:searchListAmt";
				sOutDataSet  = "dsListAmt=dsListAmt";
			}

			//조회조건 : 
			fcd_AddParam("CAL_FROM",	calReqStrDt.Value);
			fcd_AddParam("CAL_TO",		calReqEndDt.Value);
			fcd_AddParam("GUBUN", 		cboGubun.Value);
			fcd_AddParam("BRNC_CD", 	dsList.GetColumn(nRow, "COMM_NO"));

			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

		} else if (tab0.TabIndex == 1) {

			if(dsList.GetColumn(nRow, "COMM_NO") == '01'){
				sServiceName = "rbs9230-service:searchListCntBonjang";
				sOutDataSet  = "dsListAmt=dsListAmt";
			}else{
				sServiceName = "rbs9230-service:searchListCnt";
				sOutDataSet  = "dsListCnt=dsListCnt";
			}

			//조회조건 : 
			fcd_AddParam("CAL_FROM",	calReqStrDt.Value);
			fcd_AddParam("CAL_TO",		calReqEndDt.Value);
			fcd_AddParam("GUBUN", 		cboGubun.Value);
			fcd_AddParam("BRNC_CD", 	dsList.GetColumn(nRow, "COMM_NO"));

			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
		}
	}	
}

/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {

	var sExcelList = "tab0.tab1.grdListYy:발매건당 평균 배팅액";
	if (tab0.TabIndex == 0) {
		sExcelList = "tab0.tab1.grdListAmt:전자카드 금액대별 이용율 조회(금액)";
	}
	if (tab0.TabIndex == 1) {
		sExcelList = "tab0.tab2.grdListCnt:전자카드 금액대별 이용율 조회(건수)";
	}

	fcd_OpenExcelListExport(sExcelList);
}


