/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 그룹코드조회 완료 후
	if ( sServiceName == "rbs9230-service:searchList" ) {
		if(dsList.GetRowCount() > 0){
			grdList_OnCellClick();
		}
	}

	if ( sServiceName == "rbs9230-service:searchListAmt" || sServiceName == "rbs9230-service:searchListAmtBonjang" ) {
		if(dsListAmt.GetRowCount() > 0){

			//금액 데이터 추출
			var CASH_AMOUNT1 = dsListAmt.GetColumn(0, "AMOUNT1");
			var GREENCARD_AMOUNT1 = dsListAmt.GetColumn(1, "AMOUNT1");
			var GREENCARDAPP_AMOUNT1 = dsListAmt.GetColumn(2, "AMOUNT1");
			var AMOUNT1_SUM = CASH_AMOUNT1 + GREENCARD_AMOUNT1 + GREENCARDAPP_AMOUNT1;

			var CASH_AMOUNT2 = dsListAmt.GetColumn(0, "AMOUNT2");
			var GREENCARD_AMOUNT2 = dsListAmt.GetColumn(1, "AMOUNT2");
			var GREENCARDAPP_AMOUNT2 = dsListAmt.GetColumn(2, "AMOUNT2");
			var AMOUNT2_SUM = CASH_AMOUNT2 + GREENCARD_AMOUNT2 + GREENCARDAPP_AMOUNT2;

			var CASH_AMOUNT3 = dsListAmt.GetColumn(0, "AMOUNT3");
			var GREENCARD_AMOUNT3 = dsListAmt.GetColumn(1, "AMOUNT3");
			var GREENCARDAPP_AMOUNT3 = dsListAmt.GetColumn(2, "AMOUNT3");
			var AMOUNT3_SUM = CASH_AMOUNT3 + GREENCARD_AMOUNT3 + GREENCARDAPP_AMOUNT3;

			var CASH_AMOUNT4 = dsListAmt.GetColumn(0, "AMOUNT4");
			var GREENCARD_AMOUNT4 = dsListAmt.GetColumn(1, "AMOUNT4");
			var GREENCARDAPP_AMOUNT4 = dsListAmt.GetColumn(2, "AMOUNT4");
			var AMOUNT4_SUM = CASH_AMOUNT4 + GREENCARD_AMOUNT4 + GREENCARDAPP_AMOUNT4;

			var CASH_AMOUNT5 = dsListAmt.GetColumn(0, "AMOUNT5");
			var GREENCARD_AMOUNT5 = dsListAmt.GetColumn(1, "AMOUNT5");
			var GREENCARDAPP_AMOUNT5 = dsListAmt.GetColumn(2, "AMOUNT5");
			var AMOUNT5_SUM = CASH_AMOUNT5 + GREENCARD_AMOUNT5 + GREENCARDAPP_AMOUNT5;

			var CASH_AMOUNT6 = dsListAmt.GetColumn(0, "AMOUNT6");
			var GREENCARD_AMOUNT6 = dsListAmt.GetColumn(1, "AMOUNT6");
			var GREENCARDAPP_AMOUNT6 = dsListAmt.GetColumn(2, "AMOUNT6");
			var AMOUNT6_SUM = CASH_AMOUNT6 + GREENCARD_AMOUNT6 + GREENCARDAPP_AMOUNT6;

			var CASH_AMOUNT7 = dsListAmt.GetColumn(0, "AMOUNT7");
			var GREENCARD_AMOUNT7 = dsListAmt.GetColumn(1, "AMOUNT7");
			var GREENCARDAPP_AMOUNT7 = dsListAmt.GetColumn(2, "AMOUNT7");
			var AMOUNT7_SUM = CASH_AMOUNT7 + GREENCARD_AMOUNT7 + GREENCARDAPP_AMOUNT7;

			var CASH_AMOUNT8 = dsListAmt.GetColumn(0, "AMOUNT8");
			var GREENCARD_AMOUNT8 = dsListAmt.GetColumn(1, "AMOUNT8");
			var GREENCARDAPP_AMOUNT8 = dsListAmt.GetColumn(2, "AMOUNT8");
			var AMOUNT8_SUM = CASH_AMOUNT8 + GREENCARD_AMOUNT8 + GREENCARDAPP_AMOUNT8;

			//금액 비율 계산
			var AMOUNT1_RATE_SUM = round(AMOUNT1_SUM / AMOUNT8_SUM * 100, 2);
			var AMOUNT2_RATE_SUM = round(AMOUNT2_SUM / AMOUNT8_SUM * 100, 2);
			var AMOUNT3_RATE_SUM = round(AMOUNT3_SUM / AMOUNT8_SUM * 100, 2);
			var AMOUNT4_RATE_SUM = round(AMOUNT4_SUM / AMOUNT8_SUM * 100, 2);
			var AMOUNT5_RATE_SUM = round(AMOUNT5_SUM / AMOUNT8_SUM * 100, 2);
			var AMOUNT6_RATE_SUM = round(AMOUNT6_SUM / AMOUNT8_SUM * 100, 2);
			var AMOUNT7_RATE_SUM = round(AMOUNT7_SUM / AMOUNT8_SUM * 100, 2);

			dsListAmt.AddColumn("AMOUNT1_RATE_SUM");
			dsListAmt.AddColumn("AMOUNT2_RATE_SUM");
			dsListAmt.AddColumn("AMOUNT3_RATE_SUM");
			dsListAmt.AddColumn("AMOUNT4_RATE_SUM");
			dsListAmt.AddColumn("AMOUNT5_RATE_SUM");
			dsListAmt.AddColumn("AMOUNT6_RATE_SUM");
			dsListAmt.AddColumn("AMOUNT7_RATE_SUM");

			Tab0.tab1.grdListAmt.SetCellProp('Summ', 2, "text", AMOUNT1_RATE_SUM);
			Tab0.tab1.grdListAmt.SetCellProp('Summ', 4, "text", AMOUNT2_RATE_SUM);
			Tab0.tab1.grdListAmt.SetCellProp('Summ', 6, "text", AMOUNT3_RATE_SUM);
			Tab0.tab1.grdListAmt.SetCellProp('Summ', 8, "text", AMOUNT4_RATE_SUM);
			Tab0.tab1.grdListAmt.SetCellProp('Summ', 10, "text", AMOUNT5_RATE_SUM);
			Tab0.tab1.grdListAmt.SetCellProp('Summ', 12, "text", AMOUNT6_RATE_SUM);
			Tab0.tab1.grdListAmt.SetCellProp('Summ', 14, "text", AMOUNT7_RATE_SUM);
		}
	}

	if ( sServiceName == "rbs9230-service:searchListCnt" || sServiceName == "rbs9230-service:searchListCntBonjang") {
		if(dsListCnt.GetRowCount() > 0){

			//건수 데이터 추출
			var CASH_COUNT1 = dsListCnt.GetColumn(0, "COUNT1");
			var GREENCARD_COUNT1 = dsListCnt.GetColumn(1, "COUNT1");
			var GREENCARDAPP_COUNT1 = dsListCnt.GetColumn(2, "COUNT1");
			var COUNT1_SUM = CASH_COUNT1 + GREENCARD_COUNT1 + GREENCARDAPP_COUNT1;

			var CASH_COUNT2 = dsListCnt.GetColumn(0, "COUNT2");
			var GREENCARD_COUNT2 = dsListCnt.GetColumn(1, "COUNT2");
			var GREENCARDAPP_COUNT2 = dsListCnt.GetColumn(2, "COUNT2");
			var COUNT2_SUM = CASH_COUNT2 + GREENCARD_COUNT2 + GREENCARDAPP_COUNT2;

			var CASH_COUNT3 = dsListCnt.GetColumn(0, "COUNT3");
			var GREENCARD_COUNT3 = dsListCnt.GetColumn(1, "COUNT3");
			var GREENCARDAPP_COUNT3 = dsListCnt.GetColumn(2, "COUNT3");
			var COUNT3_SUM = CASH_COUNT3 + GREENCARD_COUNT3 + GREENCARDAPP_COUNT3;

			var CASH_COUNT4 = dsListCnt.GetColumn(0, "COUNT4");
			var GREENCARD_COUNT4 = dsListCnt.GetColumn(1, "COUNT4");
			var GREENCARDAPP_COUNT4 = dsListCnt.GetColumn(2, "COUNT4");
			var COUNT4_SUM = CASH_COUNT4 + GREENCARD_COUNT4 + GREENCARDAPP_COUNT4;

			var CASH_COUNT5 = dsListCnt.GetColumn(0, "COUNT5");
			var GREENCARD_COUNT5 = dsListCnt.GetColumn(1, "COUNT5");
			var GREENCARDAPP_COUNT5 = dsListCnt.GetColumn(2, "COUNT5");
			var COUNT5_SUM = CASH_COUNT5 + GREENCARD_COUNT5 + GREENCARDAPP_COUNT5;

			var CASH_COUNT6 = dsListCnt.GetColumn(0, "COUNT6");
			var GREENCARD_COUNT6 = dsListCnt.GetColumn(1, "COUNT6");
			var GREENCARDAPP_COUNT6 = dsListCnt.GetColumn(2, "COUNT6");
			var COUNT6_SUM = CASH_COUNT6 + GREENCARD_COUNT6 + GREENCARDAPP_COUNT6;

			var CASH_COUNT7 = dsListCnt.GetColumn(0, "COUNT7");
			var GREENCARD_COUNT7 = dsListCnt.GetColumn(1, "COUNT7");
			var GREENCARDAPP_COUNT7 = dsListCnt.GetColumn(2, "COUNT7");
			var COUNT7_SUM = CASH_COUNT7 + GREENCARD_COUNT7 + GREENCARDAPP_COUNT7;

			var CASH_COUNT8 = dsListCnt.GetColumn(0, "COUNT8");
			var GREENCARD_COUNT8 = dsListCnt.GetColumn(1, "COUNT8");
			var GREENCARDAPP_COUNT8 = dsListCnt.GetColumn(2, "COUNT8");
			var COUNT8_SUM = CASH_COUNT8 + GREENCARD_COUNT8 + GREENCARDAPP_COUNT8;

			//건수 비율 계산
			var COUNT1_RATE_SUM = round(COUNT1_SUM / COUNT8_SUM * 100, 2);
			var COUNT2_RATE_SUM = round(COUNT2_SUM / COUNT8_SUM * 100, 2);
			var COUNT3_RATE_SUM = round(COUNT3_SUM / COUNT8_SUM * 100, 2);
			var COUNT4_RATE_SUM = round(COUNT4_SUM / COUNT8_SUM * 100, 2);
			var COUNT5_RATE_SUM = round(COUNT5_SUM / COUNT8_SUM * 100, 2);
			var COUNT6_RATE_SUM = round(COUNT6_SUM / COUNT8_SUM * 100, 2);
			var COUNT7_RATE_SUM = round(COUNT7_SUM / COUNT8_SUM * 100, 2);

			dsListCnt.AddColumn("COUNT1_RATE_SUM");
			dsListCnt.AddColumn("COUNT2_RATE_SUM");
			dsListCnt.AddColumn("COUNT3_RATE_SUM");
			dsListCnt.AddColumn("COUNT4_RATE_SUM");
			dsListCnt.AddColumn("COUNT5_RATE_SUM");
			dsListCnt.AddColumn("COUNT6_RATE_SUM");
			dsListCnt.AddColumn("COUNT7_RATE_SUM");

			Tab0.tab2.grdListCnt.SetCellProp('Summ', 2, "text", COUNT1_RATE_SUM);
			Tab0.tab2.grdListCnt.SetCellProp('Summ', 4, "text", COUNT2_RATE_SUM);
			Tab0.tab2.grdListCnt.SetCellProp('Summ', 6, "text", COUNT3_RATE_SUM);
			Tab0.tab2.grdListCnt.SetCellProp('Summ', 8, "text", COUNT4_RATE_SUM);
			Tab0.tab2.grdListCnt.SetCellProp('Summ', 10, "text", COUNT5_RATE_SUM);
			Tab0.tab2.grdListCnt.SetCellProp('Summ', 12, "text", COUNT6_RATE_SUM);
			Tab0.tab2.grdListCnt.SetCellProp('Summ', 14, "text", COUNT7_RATE_SUM);
		}
	}
}

