/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = "TTTFTFT";

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

    //공통코드 조회
	fcd_GetComboDs(dsInComCd);

	// 년도 설정
	fcSetYear();

	cboYear.SetFocus();

	fcSearch();
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	// 자료 변경 여부를 체크하고자 하는 DataSet Object를 넘긴다.
	var sDataset = "dsLeaseMana";
	if (   !fnc_FormUnloadCheck(sDataset  , sFormId) ) return false;
}

/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	// Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}
}

/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
	dsLeaseMana.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
	if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rbs9240-service:searchLeaseMana";
	var sInDataSet   = "";
	var sOutDataSet  = "dsLeaseMana=dsLeaseMana ";

	fcd_AddParam("ACT_YEAR",	cboYear.Value);
	fcd_AddParam("BRNC_CD",		cboBrncCd.Value);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsLeaseMana)) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
		}


	// 필수항목체크
	oDataSet          = dsLeaseMana;
	sMandatoryColID   = "STND_YEAR|BRNC_CD";
	sMandatoryColName = "년도|지점코드";
	oGrid             = grdLeaseMana;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}


	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 중복 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveDualCheck(obj){

	var valiChk = true;

	for(var i=0; dsLeaseMana.rowcount; i++){
		for(var j=0; dsLeaseMana.rowcount; j++){
			if(i!=j && dsLeaseMana.GetColumn(i, "STND_YEAR") = dsLeaseMana.GetColumn(j, "STND_YEAR") &&
				dsLeaseMana.GetColumn(i, "BRNC_CD") = dsLeaseMana.GetColumn(j, "BRNC_CD")){
					alert("중복된 지점코드가 있습니다. \n 다시 확인해 주세요.");

					valiChk = false;
					break;	
			}
		}

		if(valiChk == false){
			break;
		}
	}

	return valiChk;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {
	if ( !fcSaveCheck() ) return;

	//if ( !fcSaveDualCheck() ) return;

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	sServiceName = "rbs9240-service:saveLeaseMana";
	sInDataSet   = "dsLeaseMana=dsLeaseMana:u ";
	sOutDataSet  = "dsLeaseMana=dsLeaseMana ";

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}

