	grdLeaseMana.SetFocus(); // 입력 첫 항목으로 Focus
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	if ( !fnc_DeleteCheck(dsLeaseMana, "chk", "1") ) return;
}


