>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// IP 조회 완료 후
	if ( sServiceName == "rbs9240-service:searchLeaseMana" ) {

	}


	// IP 저장 완료 후
	if ( sServiceName == "rbs9240-service:saveLeaseMana" ) {
		fcSearch();
	}
}


/*-------------------------------------------------------------------------------------------------+
>> dataset OnLoad 이벤트 처리 
+-------------------------------------------------------------------------------------------------*/
function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{

	switch ( obj.id ) {
		case "dsSearchBrncCd" :
			dsSearchBrncCd.InsertRow(0);
			dsSearchBrncCd.SetColumn(0, "CD_NM", "전체");
			cboBrncCd.index = 0;
			break;
		case "dsBrncCd" :
			//dsBrncCd.InsertRow(0);
			//dsBrncCd.SetColumn(0, "CD", "");
			//dsBrncCd.SetColumn(0, "CD_NM", "");
			break;
	}
}


