			if(varOldValue != varNewValue){
				var TOT_MACH = toNumber(grdLeaseMana.GetCellText("body",nRow,5)) + toNumber(grdLeaseMana.GetCellText("body",nRow,6)) + toNumber(grdLeaseMana.GetCellText("body",nRow,7));
				var GRN_CRD_RATE = round(toNumber(grdLeaseMana.GetCellText("body",nRow,7)) / (toNumber(grdLeaseMana.GetCellText("body",nRow,5)) + toNumber(grdLeaseMana.GetCellText("body",nRow,6)) + toNumber(grdLeaseMana.GetCellText("body",nRow,7))) * 100, 2);

