	var yearBrnc = "";
	var curYearBrnc = "";

	if ( dsLeaseMana.GetRowType(nRow) == "insert" ) {
		curYearBrnc = "|"+dsLeaseMana.GetColumn(dsLeaseMana.currow, "STND_YEAR")+":"+dsLeaseMana.GetColumn(dsLeaseMana.currow, "BRNC_CD")+"|";

		for(var i=0; i<dsLeaseMana.rowcount; i++){
			if(i==dsLeaseMana.currow) continue;
			yearBrnc += "|"+dsLeaseMana.GetColumn(i, "STND_YEAR")+":"+dsLeaseMana.GetColumn(i, "BRNC_CD")+"|";
		}

		if(IndexOf(yearBrnc, curYearBrnc)!=-1){
			alert("중복된 지점코드가 있습니다. \n 다시 확인해 주세요.");
			dsLeaseMana.SetColumn(dsLeaseMana.currow, "BRNC_CD", "");
		}
	}
