function fcInit(obj) {
	dsList.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	var sServiceName = "rbs9310-service:searchList";
	var sInDataSet   = "";
	var sOutDataSet  = "dsList=dsList";

	fcd_AddParam("STND_YEAR",	cboYear.Value);
	fcd_AddParam("STND_MM", 	cboMm.Value);
	fcd_AddParam("BRNC_CD", 	cboSearchBrncCd.Value);
	fcd_AddParam("MEET_CD", 	cboMeetCd.Value);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsList) ) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsList;
	sMandatoryColID   = "STND_YEAR|STND_MM|BRNC_CD|MEET_CD";
	sMandatoryColName = "년도|월|지점|구분";
	oGrid             = grdList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj, nRow) {

	if ( !fcSaveCheck() ) return;

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	sServiceName = "rbs9310-service:saveList";
	sInDataSet   = "dsList=dsList:u ";
	//sOutDataSet  = "dsGrpCd=dsGrpCd ";

	//첨부파일 서버 업로드 	
	for (var i = 0 ; i < dsList.GetRowCount() ; i++ ) {
		if(dsList.GetRowType(i) != "normal" && !fnc_IsNull(dsList.GetColumn(i, "FILE_NM"))) {		
			var rtn_arr = fnc_FileWrite(  divBtnLst.HttpFile 
										, dsList.GetColumn(i, "FILE_PATH" )
										, dsList.GetColumn(i, "FILE_NM" ) 
										,"");


			var fileSize = divBtnLst.HttpFile.GetFileSizeLocal(dsList.GetColumn(i, "FILE_PATH" ));

			if ( rtn_arr[0] != "SUCC" ) {
				fnc_Message(SNIS_COM_1050, "N", rtn_arr[1]);
				return false;
			}

			var filePath2 = Replace(rtn_arr[1], "\\" , "/");

			dsList.SetColumn(i, "FILE_PATH", filePath2 );	
		}else{

		}
	}

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}


