	var yearBrnc = "";
	var curYearBrnc = "";

	if ( dsList.GetRowType(nRow) == "insert" ) {
		curYearBrnc = "|"+dsList.GetColumn(dsList.currow, "STND_YEAR")+":"+dsList.GetColumn(dsList.currow, "STND_MM")+":"+dsList.GetColumn(dsList.currow, "MEET_CD")+":"+dsList.GetColumn(dsList.currow, "BRNC_CD")+"|";

		for(var i=0; i<dsList.rowcount; i++){
			if(i==dsList.currow) continue;
			yearBrnc += "|"+dsList.GetColumn(i, "STND_YEAR")+":"+dsList.GetColumn(i, "STND_MM")+":"+dsList.GetColumn(i, "MEET_CD")+":"+dsList.GetColumn(i, "BRNC_CD")+"|";
		}

		if(IndexOf(yearBrnc, curYearBrnc)!=-1){
			alert("중복된 지점코드가 있습니다. \n 다시 확인해 주세요.");
			dsList.SetColumn(dsList.currow, "BRNC_CD", "");
		}
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  파일 찾기
+-------------------------------------------------------------------------------------------------*/
function fcPcUpOnExpandUp(obj,nRow,nCell,strVal,nPivotIndex)
{
	if(nCell == 15){
		divBtnLst.fldImage.FileName = "";               //다이얼로그 파일명 초기화

		divBtnLst.fldImage.Open();

		var FILE_PATH = divBtnLst.fldImage.FilePath + "\\" + divBtnLst.fldImage.FileName;
		var FILE_NM = divBtnLst.fldImage.FileName;

		divBtnLst.flImage.Open ( "t+" ); // t: text 모드, +: 읽기/쓰기

		SetWaitCursor(true); // 시스템의 Mouse Cursor를 모래시계(Wait Cursor)로 설정하거나 해제하는 Form API 입니다
		SetCapture();		// 마우스의 이동/ 눌림을 가로채서 Script와 화면에 도달하지 못하도록 하는 Form API 입니다.

		dsList.SetColumn(nRow, "FILE_PATH", FILE_PATH);
		dsList.SetColumn(nRow, "FILE_NM", FILE_NM);

		divBtnLst.flImage.Close();

		SetWaitCursor(false);
		ReleaseCapture();
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  파일 다운받기
+-------------------------------------------------------------------------------------------------*/
function fcGrdOnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	switch ( obj.id ) 
	{	
		case "grdList" : 

			if(nCell == 15){
				var sLocalFileName  = fcd_GetSelectPCFile("Save", dsList.GetColumn(nRow, "FILE_NM")); // 파일명
				var sServerFileName    = dsList.GetColumn(nRow, "FILE_PATH");	// 파일 경로
				var sFileSize       = 0;

				if ( sLocalFileName == null ) return;

				fnc_PCFileRead(divBtnLst.HttpFile, sLocalFileName[0] + "\\" + sLocalFileName[1], sServerFileName, sFileSize, nRow, "PROG_BAR", dsList);
				break;
			}

	}
}

