*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
	dsList.ClearData();
}

function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";
	if ( !fcSearchCheck(obj) ) return;

	if (tab0.TabIndex == 0) {
		sServiceName = "rbs9320-service:searchList";
		sOutDataSet  = "dsList=dsList ";

		//조회조건 : 
		fcd_AddParam("STND_YEAR",	cboYear.Value);
		fcd_AddParam("STND_MM", 	cboMm.Value);
		fcd_AddParam("BRNC_CD", 	cboSearchBrncCd.Value);
		fcd_AddParam("MEET_CD", 	cboMeetCd.Value);

	} else if (tab0.TabIndex == 1) {
		sServiceName = "rbs9320-service:searchListMm";
		sOutDataSet  = "dsListMm=dsListMm ";

		//조회조건 : 
		fcd_AddParam("STND_YEAR",	cboYear.Value);
		fcd_AddParam("STND_MM", 	cboMm.Value);
		fcd_AddParam("MEET_CD", 	cboMeetCd.Value);

	} else if (tab0.TabIndex == 2) {
		sServiceName = "rbs9320-service:searchListYy";
		sOutDataSet  = "dsListYy=dsListYy ";

		//조회조건 : 
		fcd_AddParam("STND_YEAR",	cboYear.Value);
		fcd_AddParam("MEET_CD", 	cboMeetCd.Value);
	} 

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {

	var sExcelList = "tab0.tab1.grdList:지점별 적발건수 현황";
	if (tab0.TabIndex == 1) {
		sExcelList = "tab0.tab2.grdListMm:월별 적발건수 현황";
	} else if (tab0.TabIndex == 2) {
		sExcelList = "tab0.tab3.grdListYy:연도별 적발건수 현황";
	}

	fcd_OpenExcelListExport(sExcelList);
}


/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
