function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	switch ( obj.id ) {

		case "dsBrncCd":

			dsBrncCd.InsertRow(0);
			dsBrncCd.SetColumn(0, "CD_NM", "전체");
			cboSearchBrncCd.index = 0;

			break;	

	}
}

/*-------------------------------------------------------------------------------------------------+
>>  활동년도 설정 2011 년~  
+-------------------------------------------------------------------------------------------------*/
function fcSetYear(){

	// 년도 셋팅
	var sCurYear  = ParseInt(fcd_GetSysdate("YYYY"));

	for(var i=sCurYear; i >= sCurYear - 5; i--){
		var nRow = dsYear.AddRow();

		dsYear.SetColumn(nRow,"CD"   ,i);
		dsYear.SetColumn(nRow,"CD_NM",i);	
	}
}

/*-------------------------------------------------------------------------------------------------+
>> Enter Key 이벤트시 조회되도록 한다.
+-------------------------------------------------------------------------------------------------*/
function fcEdOnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if ( nChar == ENTER_KEY ) {		
		fcSearch();				
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  파일 찾기
+-------------------------------------------------------------------------------------------------*/
function fcPcUpOnExpandUp(obj,nRow,nCell,strVal,nPivotIndex)
{
	switch ( obj.id ) 
	{	
		case "grdListMm" :
			if(nCell == 10){
				divBtnLst.fldImage.FileName = "";               //다이얼로그 파일명 초기화

				divBtnLst.fldImage.Open();

				var FILE_PATH = divBtnLst.fldImage.FilePath + "\\" + divBtnLst.fldImage.FileName;
				var FILE_NM = divBtnLst.fldImage.FileName;

				divBtnLst.flImage.Open ( "t+" ); // t: text 모드, +: 읽기/쓰기

				SetWaitCursor(true); // 시스템의 Mouse Cursor를 모래시계(Wait Cursor)로 설정하거나 해제하는 Form API 입니다
				SetCapture();		// 마우스의 이동/ 눌림을 가로채서 Script와 화면에 도달하지 못하도록 하는 Form API 입니다.

				dsListMm.SetColumn(nRow, "FILE_PATH", FILE_PATH);
				dsListMm.SetColumn(nRow, "FILE_NM", FILE_NM);

				divBtnLst.flImage.Close();

				SetWaitCursor(false);
				ReleaseCapture();
			}
	}
}


/*-------------------------------------------------------------------------------------------------+
>>  파일 다운받기
+-------------------------------------------------------------------------------------------------*/
function fcGrdOnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	switch ( obj.id ) 
	{	
		case "grdListMm" : 
			if(nCell == 10){
				var sLocalFileName  = fcd_GetSelectPCFile("Save", dsListMm.GetColumn(nRow, "FILE_NM")); // 파일명
				var sServerFileName = dsListMm.GetColumn(nRow, "FILE_PATH");	// 파일 경로
				var sFileSize       = 0;

				if ( sLocalFileName == null ) return;

				fnc_PCFileRead(divBtnLst.HttpFile, sLocalFileName[0] + "\\" + sLocalFileName[1], sServerFileName, sFileSize, nRow, "PROG_BAR", dsListMm);
				break;
			}
	}

}

