/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 입장인원통계 - 경주별 조회 완료 후
	if ( sServiceName == "rem3020-service:searchStayStatByRace" ) {


	}


}

function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{

	switch ( obj.id ) {
		case "cboSearchBrncCd":

			fcSearch();

			break;

	}
}

function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	switch ( obj.id ) {
		case "dsMeetCd":

				rdoMeetCd.Value = "001";

			break;

		case "dsBrncCd":

			dsBrncCd.InsertRow(0);
			dsBrncCd.SetColumn(0, "CD_NM", "전체");
			cboSearchBrncCd.index = 0;

			break;	

	}
}

function fcRdoOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch ( obj.id ) {
		case "rdoMeetCd":
			fcSetCellByMeetCd();
			fcSearch();
			break;

	}
}




function fcSetCellByMeetCd(){
	var sMeetCd = rdoMeetCd.Value;

	var sCellStr = ""; // 3:월, 4:화 ,5:수,6:목,7:금 ,8:토, 9:일
	var nCellWidth = 0;


	if(sMeetCd == "001"){
		sCellStr = "7,8,9";
		nCellWidth = 138;

		grdStayStatByRace.SetColProp(1,"WIDTH",137);

	}else if(sMeetCd == "003"){
		sCellStr = "4,5,6,9";
		nCellWidth = 110;

		grdStayStatByRace.SetColProp(1,"WIDTH",139);
	}

	//초기화
	for(var i = 3; i <=9 ; i++){
		grdStayStatByRace.SetColProp(i,"WIDTH",0);
	}

	var aCell = split(sCellStr,",");

	for(var i =0; i <=aCell.length; i++){

		//grdStayStatByRace.SetColProp(aCell[i],"WIDTH",107);
		grdStayStatByRace.SetColProp(aCell[i],"WIDTH",nCellWidth);
	}

}
