/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  

    sBUTTONLIST   = 'TTFFTFT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

    //공통코드 조회
	fcd_GetComboDs(dsInComCd);

	calSearchRaceDt =  AddDate(Today(),-1);
	fcGetRaceTms();

	fcSetCellByMeetCd();

}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	// 자료 변경 여부를 체크하고자 하는 DataSet Object를 넘긴다.
	var sDataset = "dsStayStatByRace";
	if (   !fnc_FormUnloadCheck(sDataset  , sFormId) ) return false;
}

/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	// Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}


}

/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {

	dsStayStatByRace.ClearData();

}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}



/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";
	if ( !fcSearchCheck(obj) ) return;


	 sServiceName = "rem3021-service:searchStayStatByRaceCompare";
	 sInDataSet   = "";
	 sOutDataSet  = "dsStayStatByRace=dsStayStatByRace ";
//Trace(dsRaceTms.savexml());
	 //조회조건 : 
	 fcd_AddParam("MEET_CD",   rdoMeetCd.Value);
	 fcd_AddParam("BRNC_CD",   cboSearchBrncCd.Value);
	 fcd_AddParam("STND_YEAR", dsRaceTms.GetColumn(0, "STND_YEAR"));
	 fcd_Addparam("TMS", 	   dsRaceTms.GetColumn(0, "TMS"));

	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

function fcGetRaceTms()
{

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	 sServiceName = "rem3021-service:searchRaceTms";
	 sInDataSet   = "";
	 sOutDataSet  = "dsRaceTms=dsRaceTms ";

	 //조회조건 : 
	 fcd_AddParam("RACE_DAY", calSearchRaceDt.Value);

	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
		var sExcelList = "grdStayStatByRace:입장인원통계 경주별현황";

		fcd_OpenExcelListExport(sExcelList);
}


/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	// 화면 종료
	Close();
}



