/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 입장인원통계 - 경주별 조회 완료 후
	if ( sServiceName == "common-service:searchCode" ) {
		if (GdsUser.GetColumn(0, "BRNC_CD") != "00")
			cboSearchBrncCd.Value = GdsUser.GetColumn(0, "BRNC_CD");		
	}

	// 회차 조회 완료 후
	if ( sServiceName == "rem3021-service:searchRaceTms" ) {
		rdoMeetCd.Value = dsRaceTms.GetColumn(0, "MEET_CD");
		fcSetCellByMeetCd();
		fcSearch();
	}
	// 입장인원통계 - 경주별 조회 완료 후
	if ( sServiceName == "rem3021-service:searchStayStatByRaceCompare" ) {

	}


}

function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{

	switch ( obj.id ) {
		case "cboSearchBrncCd":

			fcSearch();

			break;

	}
}

function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	switch ( obj.id ) {
		case "dsMeetCd":

				rdoMeetCd.Value = "001";

			break;

		case "dsBrncCd":

			dsBrncCd.InsertRow(0);
			dsBrncCd.SetColumn(0, "CD_NM", "전체");
			cboSearchBrncCd.index = 0;

			break;	

	}
}

function fcRdoOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch ( obj.id ) {
		case "rdoMeetCd":
			fcSetCellByMeetCd();
			fcSearch();
			break;

	}
}

function fcSetCellByMeetCd(){
	//dsStayStatByRace.fireEvent = false;
	grdStayStatByRace.Redraw = false;

	var sMeetCd = rdoMeetCd.Value;
	var sTms    = toNumber(dsRaceTms.GetColumn(0, "TMS"));
	var sText   = "";

	var sCellStr = ""; // 3:월, 4:화 ,5:수,6:목,7:금 ,8:토, 9:일
	var nCellWidth = 0;
	var stri, endi;


	if(sMeetCd == "001"){
		sCellStr = "0,7,8,9,10,11,12,13,14,15";
		nCellWidth = 80;
		stri = 12;
		endi = 22;

		//grdStayStatByRace.SetColProp(0,"WIDTH",80);

	}else if(sMeetCd == "003"){
		sCellStr = "0,1,2,3,4,5,6";
		nCellWidth = 160;
		stri = 6;
		endi = 12;

		//grdStayStatByRace.SetColProp(0,"WIDTH",160);
	}

	//초기화
	for(var i = 1; i <=15 ; i++){
		grdStayStatByRace.SetColProp(i,"WIDTH",0);
	}

	var aCell = split(sCellStr,",");

	var abstactNo = 0;
		Trace("sTms="+sTms);

	for(var i =0; i <=aCell.length; i++){
		grdStayStatByRace.SetColProp(aCell[i],"WIDTH",nCellWidth);		

		abstactNo = 2 - (i%3);			
		sText = toString(sTms - abstactNo)+"회";

		Trace("aCell[i]="+aCell[i]+", sText="+sText);
		//grdStayStatByRace.SetCellProp("head", aCell[i], "TEXT", sText);
	}
	j=0;
	for(var i =stri; i <endi; i++) {			
		abstactNo = 2 - (j%3);			
		sText = toString(sTms - abstactNo)+"회";

		grdStayStatByRace.SetCellProp("head", i, "TEXT", sText);
		j++;
	}
	/*
	j=0;
	if (sMeetCd == "001") {
		for(var i =12; i <22; i++){			
			abstactNo = 2 - (j%3);			
			sText = toString(sTms - abstactNo)+"회";

			Trace("aCell[i]="+i+", sText="+sText);
			grdStayStatByRace.SetCellProp("head", i, "TEXT", sText);
			j++;
		}
	} else {
		for(var i =6; i <12; i++){			
			abstactNo = 2 - (j%3);			
			sText = toString(sTms - abstactNo)+"회";
			Trace("i="+i+", j="+j+", sText="+sText);
			grdStayStatByRace.SetCellProp("head", i, "TEXT", sText);
			j++;
		}
	}
	*/
	grdStayStatByRace.Redraw = true; 
}


function fcCalOnChange(obj,strOldText,strNewText)
{
	if (obj.id == "calSearchRaceDt") {
		fcGetRaceTms();
	}
}
