/***************************************************************************************************
*                                         공통 Event 처리 부분                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  

    sBUTTONLIST   = 'TTFFTFT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);


    //공통코드 조회
	fcd_GetComboDs(dsInComCd);

	//var sCurYear = fcd_GetSysdate("YYYY");	
	calRaceStrDt.Value = AddDate(today(), -30); 
	calRaceEndDt.Value = AddDate(today(), -1);

    fcSearch();
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	// 자료 변경 여부를 체크하고자 하는 DataSet Object를 넘긴다.
	var sDataset = "dsSendStatus";
	if (   !fnc_FormUnloadCheck(sDataset  , sFormId) ) return false;
}

/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	// Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}


}

/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {

	dsSendStatus.ClearData();

}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}



/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";
	if ( !fcSearchCheck(obj) ) return;


	 sServiceName = "rem4050-service:searchSendStatus";
	 sInDataSet   = "";
	 sOutDataSet  = "dsSendStatus=dsSendStatus dsSendVeri=dsSendVeri dsLastStatus=dsLastStatus";
	 //조회조건 : 
	 fcd_AddParam("STR_DT", calRaceStrDt.Value);
	 fcd_AddParam("END_DT", calRaceEndDt.Value);

	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";
	//if ( !fcSaveCheck(obj) ) return;


	 sServiceName = "rem4050-service:saveSendVeri";
	 sInDataSet   = "dsSendVeri=dsSendVeri:u";
	 sOutDataSet  = "dsSendStatus=dsSendStatus dsSendVeri=dsSendVeri";

	 //조회조건 : 
	 fcd_AddParam("EVENT_DT", calRaceStrDt.Value);
	 fcd_AddParam("STR_DT", calRaceStrDt.Value);
	 fcd_AddParam("END_DT", calRaceEndDt.Value);

	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);	
}

/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
		var sExcelList = "grdSendStatus:입장인원 일별수신현황";

		fcd_OpenExcelListExport(sExcelList);
}


/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	// 화면 종료
	Close();
}



