}



/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 입장인원통계 - 경주별 조회 완료 후
	if ( sServiceName == "rem4050-service:searchSendStatus" ) {
		tRACE(dsLastStatus.savexml());		
	}
}

function fcOnCellDBLClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (nCell <> 0) return;
	var sTranDt = dsSendStatusPivot.GetColumn(nRow, "TRAN_DT");
	var iRow = dsSendVeri.FindRow("TRAN_DT", sTranDt);
	var sVeri = dsSendVeri.GetColumn(iRow, "VERI");
	var sMesg = "";
	if (sVeri == "002") {	// 검증이 안된 경우
		sMesg = sTranDt + "일의 입장료징수를 확정하시겠습니까?";
	} else {
		sMesg = sTranDt + "일의 입장료징수를 확정취소하시겠습니까?";
	}
	if (fnc_message(sMesg,"Y") == true) {
		if (sVeri == "002") {
			dsSendVeri.SetColumn(iRow, "VERI", "001");
		} else {
			dsSendVeri.SetColumn(iRow, "VERI", "002");
		}
		fcSave();
	}
}


function fcbtnOnClick(obj)
{
	if (obj.id == "btnVisibleLastStatus") {
		if (grdLastStatus.visible == true) {
			grdLastStatus.visible = false;
		} else {
			grdLastStatus.visible = true;
		}
	}
}
