/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 전송상태 조회 후
	if ( sServiceName == "rem4091-service:searchCronStatus" ) {

	}

	// 입장현황 수동배치 완료 후
	if ( sServiceName == "rem4091-service:SaveCronData" ) {
		dsCronStatus.ClearData();
		fcSearch();
	}

	// 입장현황 수동배치 완료 후
	if ( sServiceName == "rem4091-service:SaveSeedTest" ) {
		alert("저장 되었습니다.");
		fcSearchSeedTest();

	}

}



