function fcSearchSeedTest(obj) {

	if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rem4091-service:searchSeedTest";
	var sInDataSet   = "";
	var sOutDataSet  = "dsSeedTest=dsSeedTest ";

	fcd_SSLTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}
