/***************************************************************************************************
*                                          화면 변수 선언부                                        *
***************************************************************************************************/
var sFormId       =  this.getform().id;
var sBUTTONLIST   = 'TTTTTT';                          //공통기능정의(버튼순으로 T or F)
var sUSERAUTH     = '';                                //사용자별 프로그램 사용 권한
var sFORMCAPTION  = '';                                //Form의 Title 정보
var sFORMLOCATION = '';                                //Form의 Location 정보
var sUSERLAVEL    = '';                                //Form의 사용  Level
var vREVOBJ       = Array(9);                          //결과값 처리를 위한 오브젝트 
var sGRDTIT       = "";                                //그리드 제목
var sCUROBJ       ;                                    //현재 LOST FOCUS 오브젝트

var sMenuGrpNum     = "0";                         // 메뉴그룹의 갯수보관
var sMenuListNum    = "0";                          // 메뉴리스트의 갯수보관
var sMenuUpId       = "";
var sMenuUpNm       = "";

/***************************************************************************************************
*                                         공통 Event 처리 부분                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  

    sBUTTONLIST   = 'TT'+sInpt+'FTFT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

    //공통코드 조회
	fcd_GetComboDs(dsInComCd);	

	//지점코드
	//dsBrncCd

	//var sCurYear = fcd_GetSysdate("YYYY");	
	calRaceStrDt.Value = AddDate(today(), -1); 
	calRaceEndDt.Value = today();

	if (IsExistVar("sInpt", true)) {
		if (sInpt == "T") {
			if (toNumber(substr(GetDate(),8,2)) >= 15) 	btnImport.Visible = true;
			else 										btnImport.Visible = false;
		}
	}
    fcSearch();
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	// 자료 변경 여부를 체크하고자 하는 DataSet Object를 넘긴다.
	var sDataset = "dsStayStatByTerm";
	if (   !fnc_FormUnloadCheck(sDataset  , sFormId) ) return false;
}

/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	// Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}


}

/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {

	if (tab0.TabIndex == 0)	dsStayStatByTerm.ClearData();
	else                    dsStayStatByDaily.ClearData(); 

}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}



/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";
	if ( !fcSearchCheck(obj) ) return;

	if (tab0.TabIndex == 0) {
		sServiceName = "rem5010-service:searchFixedSeatByTerm";
		sInDataSet   = "";
		sOutDataSet  = "dsStayStatByTerm=dsStayStatByTerm ";
	} else {
		sServiceName = "rem5010-service:searchFixedSeatByDaily";
		sInDataSet   = "";
		sOutDataSet  = "dsStayStatByDaily=dsStayStatByDaily ";
	}
	//조회조건 : 

	fcd_AddParam("BRNC_CD", cboSearchBrncCd.Value);
	fcd_AddParam("STR_DT", calRaceStrDt.Value);
	fcd_AddParam("END_DT", calRaceEndDt.Value);
	fcd_AddParam("EXCEPT_KM", chkExceptKM.Value);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}



/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {	
	var sExcelList = "tab0.tab1.grdStayStatByTerm:입장인원통계 기간별현황";
	if (tab0.TabIndex == 0) {
		fcd_OpenExcelListExport(sExcelList);
	} else {
		dsStayStatByDaily.FireEvent = false;
		var keyVal = dsStayStatByDaily.GroupKey;
		dsStayStatByDaily.GroupKey = "";
		sExcelList = "tab0.tab2.grdStayStatByDaily:입장인원통계 일별현황";
		fcd_OpenExcelListExport(sExcelList);
		dsStayStatByDaily.GroupKey = keyVal;
		dsStayStatByDaily.FireEvent = true;
	}
}


/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	// 화면 종료
	Close();
}



