	// 특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)

		fnc_GridSort(obj, object(obj.BindDataset), nCell);

}

/*-------------------------------------------------------------------------------------------------+
>>  Help Popup Open
+-------------------------------------------------------------------------------------------------*/
function fcHelpDialoge(obj,nX,nY) {

}



/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	// 입장인원통계 - 경주별 조회 완료 후
	if ( sServiceName == "rem5010-service:searchFixedSeatByDaily" ) {
		//if (cboSearchBrncCd.value == "20" || Mid(cboSearchBrncCd.value,0,2) == "00") {	//성북, 광명본장 지점인 경우 직접 입력 가능하게 처리
		if (cboSearchBrncCd.value == "20") {	//성북, 광명본장 지점인 경우 직접 입력 가능하게 처리
			divBtnRow.visible = true;
			tab0.tab2.grdStayStatByDaily.SetColProp(0,"Width","35");
		} else {
			divBtnRow.visible = false;
			tab0.tab2.grdStayStatByDaily.SetColProp(0,"Width","0");
		}
		fcCheckGroupKey();	
	} else if ( sServiceName == "common-service:searchCode" ) {

	} else if ( sServiceName == "rem5010-service:searchSolbi") {

	} else if ( sServiceName == "rem5010-service:importFixedSeatData") {
		fcSearch();
	}

}

function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{	
	switch ( obj.id ) {
		case "cboSearchBrncCd":					    
			setReg("BRNC_CD", cboSearchBrncCd.Value);
			fcSearch();			 
			break;
	}
}

function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	switch ( obj.id ) {		
		case "dsBrncCd":
			dsCommNo.copy(dsBrncCd);
			dsCommNo.InsertRow(0);
			dsCommNo.SetColumn(0, "CD", "%");
			dsCommNo.SetColumn(0, "CD_NM", "전체");			

			var iRow = -1;
			iRow = dsCommNo.FindRow("CD_TERM2", GdsUser.GetColumn(0, "TEAM_CD"));
			if (iRow > 0) cboSearchBrncCd.index = iRow;
			else cboSearchBrncCd.index = 0;
			cboSearchBrncCd.Value = getReg("BRNC_CD");
			break;		
	}
}



function fcCheckGroupKey()
{
	if (cboSearchBrncCd.Value == "%" && calRaceStrDt.value != calRaceEndDt.Value) 
		dsStayStatByDaily.GroupKey = "COMM_NO";
	else
		dsStayStatByDaily.GroupKey = "";
}



/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	switch( obj.GetForm().id ) {
		case "divBtnRow" :
			nRow = dsStayStatByDaily.AddRow();			
			dsStayStatByDaily.SetColumn(nRow, "COMM_NO", cboSearchBrncCd.Value);	
			dsStayStatByDaily.SetColumn(nRow, "ISSUE_DT", AddDate(today(), -1));	

			break;		
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	//직접 버튼을 클릭한 경우
	switch( obj.GetForm().id ) {
		case "divBtnRow" :
			if ( !fnc_DeleteCheck(dsStayStatByDaily, "CHK", "1") ) return;
			break;
	}	
}

function fcDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (obj.id == "dsStayStatByDaily") {
		if (strColumnId == "FS_ENT_PAY_CNT") {
			var fsEntAmt = 0;
			fsEntAmt = dsStayStatByDaily.GetColumn(nRow, "FS_ENT_PAY_CNT");		// 금액 변경시 수정해야 함...
			fsEntAmt = fsEntAmt * toNumber(dsCommNo.GetColumn(cboSearchBrncCd.Index, "CD_TERM1"));
			dsStayStatByDaily.SetColumn(nRow, "FS_ENT_AMT", fsEntAmt);
		} 
	}
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

// 저장 전에 체크
function fcSaveCheck(obj) {  

	if ( !fnc_DatasetChangeCheck(dsStayStatByDaily)) {	//변경된 자료가 있는지 체크

		fnc_Message(SNIS_COM_1006, "N");	//저장 할 자료가 없음
		return false;
	}

	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck() ) return;   

    if(fnc_Message(SNIS_COM_1012, "Y")) {           
		var sServiceName = "rem5010-service:saveFixedSeatEntrCnt";
		var sInDataSet   = "dsStayStatByDaily=dsStayStatByDaily:u ";
		var sOutDataSet  = "dsStayStatByDaily=dsStayStatByDaily ";

		fcd_AddParam("BRNC_CD", cboSearchBrncCd.Value);
		fcd_AddParam("STR_DT", calRaceStrDt.Value);
		fcd_AddParam("END_DT", calRaceEndDt.Value);
		fcd_AddParam("EXCEPT_KM", chkExceptKM.Value);

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
    }
}


function fcOnClick(obj)
{
	if (obj.id == "btnImport") {
		fcFixedSeatImport();
	}
}

function fcFixedSeatImport()
{
	fnc_message("["+btnImport.text+"]은 최대 5분전의 자료만 가능합니다.");
	var sServiceName = "rem5010-service:importFixedSeatData";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}
