/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
	if (tab0.tabindex == 0)	     	dsGoodUnStor.ClearData();	
	else if (tab0.tabindex == 1)	dsYearItemOut.ClearData();	
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}



/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";
	if ( !fcSearchCheck(obj) ) return;

	if (tab0.tabindex == 0) {
		var YrItemYn = dsCommNo.GetColumn(cboSearchBrncCd.Index, "CD_TERM3");
		if (Mid(YrItemYn,0,1) == "1") return ;	// 단가계약관리 지점에서 일반 품목 조회 불가

		sServiceName = "rem5040-service:searchGoodUnStor";
		sInDataSet   = "";
		sOutDataSet  = "dsGoodUnStor=dsGoodUnStor ";
	} else if (tab0.tabindex == 1) {
		sServiceName = "rem5040-service:searchYearItemOut";
		sInDataSet   = "";
		sOutDataSet  = "dsYearItemOut=dsYearItemOut ";
	} else {
		return;
	}

	 //조회조건 : 
	 fcd_AddParam("BRNC_CD", cboSearchBrncCd.Value);
 	 fcd_AddParam("UNSTOR_DT", calUnStorDt.Value);

	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchGoodType(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	sServiceName = "rem5020-service:searchGoodType";
	sInDataSet   = "";
	sOutDataSet  = "dsGoodType=dsGoodType ";

	//조회조건 : 
	fcd_AddParam("BRNC_CD", cboSearchBrncCd.Value);
	fcd_AddParam("USE_YN",  "Y");

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

// 저장 전에 체크
function fcSaveCheck(obj) {  

	if ( !fnc_DatasetChangeCheck(dsGoodUnStor) && !fnc_DatasetChangeCheck(dsYearItemOut)) {	//변경된 자료가 있는지 체크

		fnc_Message(SNIS_COM_1006, "N");	//저장 할 자료가 없음
		return false;
	}

	if (tab0.tabindex == 0) {

		// 필수항목체크
		oDataSet          = dsGoodUnStor;
		sMandatoryColID   = "UNSTOR_CNT|DISUSE_CNT";
		sMandatoryColName = "당일사용 수량|당일폐기 수량";
		oGrid             = tab0.tab1.grdGoodUnStor;

		if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
			return false;
		}


		dsGoodUnStor.SetUpdate(true);
		var sGoodNm = "";
		for (var iCnt=0;iCnt<dsGoodUnStor.RowCount();iCnt++) {
			if (dsGoodUnStor.GetColumn(iCnt, "UNSTOR_CNT") < 0 ||
				dsGoodUnStor.GetColumn(iCnt, "DISUSE_CNT") < 0    ) {
				sGoodNm = dsGoodUnStor.GetColumn(iCnt, "GOOD_NM");
				fnc_message(sGoodNm+"의 당일사용량 오류!!\n당일폐기 또는 당일재고의 수량을 확인 바랍니다.", "N");
				dsGoodUnStor.row = iCnt;
				return false;
			} else if (dsGoodUnStor.GetColumn(iCnt, "DISUSE_CNT") > 0 &&
					   fnc_isnull(dsGoodUnStor.GetColumn(iCnt, "RMK"))   ) {
				sGoodNm = dsGoodUnStor.GetColumn(iCnt, "GOOD_NM");
				fnc_message(sGoodNm+"의 폐기 사유를 반드시 입력해 주세요!", "N");
				dsGoodUnStor.row = iCnt;
				return false;
			}
		}
	} else if (tab0.tabindex == 1) {

		oDataSet          = dsYearItemOut;
		sMandatoryColID   = "UNSTOR_CNT|DISUSE_CNT";
		sMandatoryColName = "당일사용 수량|당일폐기 수량";
		oGrid             = tab0.tab2.grdYearItemOut;

		if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
			return false;
		}

		dsYearItemOut.SetUpdate(true);
		var sGoodNm = "";
		for (var iCnt=0;iCnt<dsYearItemOut.RowCount();iCnt++) {
			if (dsYearItemOut.GetColumn(iCnt, "UNSTOR_CNT") < 0 ||
				dsYearItemOut.GetColumn(iCnt, "DISUSE_CNT") < 0    ) {
				sGoodNm = dsYearItemOut.GetColumn(iCnt, "GOOD_NM");
				fnc_message(sGoodNm+"의 당일사용량 오류!!\n당일폐기 또는 당일재고의 수량을 확인 바랍니다.", "N");
				dsYearItemOut.row = iCnt;
				return false;
			} else if (dsYearItemOut.GetColumn(iCnt, "DISUSE_CNT") > 0 &&
					   fnc_isnull(dsYearItemOut.GetColumn(iCnt, "RMK"))   ) {
				sGoodNm = dsYearItemOut.GetColumn(iCnt, "GOOD_NM");
				fnc_message(sGoodNm+"의 폐기 사유를 반드시 입력해 주세요!", "N");
				dsYearItemOut.row = iCnt;
				return false;
			}
		}
	}
	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck() ) return;   

	if(fnc_Message(SNIS_COM_1012, "Y")) {           
		if (tab0.tabindex == 0) {
			var sServiceName = "rem5040-service:saveGoodUnStor";
			var sInDataSet   = "dsGoodUnStor=dsGoodUnStor:u ";
			var sOutDataSet  = "dsGoodUnStor=dsGoodUnStor ";

			fcd_AddParam("BRNC_CD",   cboSearchBrncCd.Value);
			fcd_AddParam("UNSTOR_DT", calUnStorDt.Value);

			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

		} else if (tab0.tabindex == 1) {
			var sServiceName = "rem5040-service:saveYearItemOut";
			var sInDataSet   = "dsYearItemOut=dsYearItemOut:u ";
			var sOutDataSet  = "dsYearItemOut=dsYearItemOut ";

			fcd_AddParam("BRNC_CD",   cboSearchBrncCd.Value);
			fcd_AddParam("UNSTOR_DT", calUnStorDt.Value);

			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
		}
	}
}


