/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	if ( sServiceName = "common-service:searchCode" ) {			// 공통코드조회후 해당 지점의 상품분류 조회
		var sEntrFee = dsCommNo.GetColumn(cboSearchBrncCd.Index, "CD_TERM1");
		sDeptCd = dsCommNo.GetColumn(cboSearchBrncCd.Index, "CD_TERM2");

		//연간단가계약품목 관리지점
		var YrItemYn = dsCommNo.GetColumn(cboSearchBrncCd.Index, "CD_TERM3");
		if (Mid(YrItemYn,0,1) == "1") {
			tab0.tabindex = 1;
			fcTabEditable(false, true); 
		} else {
			tab0.tabindex = 0;		
			fcTabEditable(true, false); 
		}
		if (toNumber(sEntrFee) == 0) {
			divBtnLst.btnSave.Enable = false;
		} 
trace("###########cboSearchBrncCd.Index:" + cboSearchBrncCd.Index);						
trace("###########YrItemYn:" + YrItemYn);						
trace("###########sDeptCd:" + sDeptCd);						
trace("###########sEntrFee:" + sEntrFee);				
trace("###########divBtnLst.btnSave.Enable:" + divBtnLst.btnSave.Enable);		
		fcSearchGoodType();
	} else if ( sServiceName = "rem5020-service:searchGoodType" ) {	// 상품분류 조회후 상품 내역 조회
		fcSearch();
	} else if ( sServiceName = "rem5040-service:searchGoodUnStor" || sServiceName = "rem5040-service:searchYearItemOut" ) {	// 상품분류 조회후 상품 내역 조회
		SettingButton();	// 권한 체크
	}	
}

function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{	
	switch ( obj.id ) {
		case "cboSearchBrncCd":		
			if (cboSearchBrncCd.Value == gdsUser.GetColumn(0, "BRNC_CD") && sApro != "T") {
				fcTabEditable(false, false);
			}
			sDeptCd = dsCommNo.GetColumn(nNewIndex, "CD_TERM2");

			//연간단가계약품목 관리지점
			var YrItemYn = dsCommNo.GetColumn(cboSearchBrncCd.Index, "CD_TERM3");
			if (Mid(YrItemYn,0,1) == "1") {
				tab0.tabindex = 1;
				fcTabEditable(false, true); 
			} else {
			 	tab0.tabindex = 0;		
				fcTabEditable(true, false ); 
			}
			dsGoodUnStor.ClearData();	
			dsYearItemOut.ClearData();	
			setReg("BRNC_CD", cboSearchBrncCd.Value);


			var sEntrFee = dsCommNo.GetColumn(cboSearchBrncCd.Index, "CD_TERM1");
			if (toNumber(sEntrFee) == 0) {
				divBtnLst.btnSave.Enable = false;
			} else if (sApro == "T") {
				divBtnLst.btnSave.Enable = true;
			}

			fcSearchGoodType();			
			break;
	}
}

function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	switch ( obj.id ) {		
		case "dsBrncCd":
			dsCommNo.copy(dsBrncCd);
			if (sApro == "T") {
				dsCommNo.InsertRow(0);
				dsCommNo.SetColumn(0, "CD", "%");
				dsCommNo.SetColumn(0, "CD_NM", "전체");
			} else {
				//cboSearchBrncCd.Value = GdsUser.GetColumn(0, "BRNC_CD");
				dsCommNo.Filter("CD_TERM2='"+GdsUser.GetColumn(0, "TEAM_CD")+"'");	
			}
			//cboSearchBrncCd.index = 0;
			cboSearchBrncCd.Value = getReg("BRNC_CD");


		break;		
	}
}




