// 권한에 따른 버튼 제어
function SettingButton()
{
	var bEditable = false;
	var btnStat = "";	
	sStatCd = "000";
	var objDS;
	if (tab0.TabIndex == 0) objDS = dsGoodUnStor;
	else                    objDS = dsYearItemOut;
	if (objDS.RowCount() >= 0) {
		// 상태에 따른 버튼 제어
		for (var iCnt=0;iCnt<objDS.rowcount();iCnt++) {
			if (objDS.GetColumn(iCnt, "STAT_CD") > sStatCd)  {
				sStatCd = objDS.GetColumn(iCnt, "STAT_CD");
				nMaxStatRow = iCnt;
			}
		}	
		if (sStatCd > "001") fcTabEditable(false, false);
		else {
			if      (tab0.TabIndex == 0) fcTabEditable(true,  false );
			else if (tab0.TabIndex == 1) fcTabEditable(false, true );
		}

		// 001:등록, 002:결재요청, 003:관리자반려, 004부서승인
		// 011:관리자 접수, 011:승인요청, 012:최종승인, 021:결과입력
		switch (sStatCd) {
			case "000":
				//btnstat = "00000";					// 저장
				//stStatNm.text = "";	
				//break;
			case "001":
			    if (objDS.GetColumn(nMaxStatRow, "APRV_ID") == gdsUser.GetColumn(0, "USER_ID")) // 결재자인 경우
					btnstat = "00000";								
				else if (sDeptCd == gdsUser.GetColumn(0, "TEAM_CD")  || sApro=="T") // 요청자와 같은 부서인  경우
					btnstat = "10000";
				stStatNm.text = "";	
				break;
			case "002":	// 지점 결재 요청
				if (objDS.GetColumn(nMaxStatRow, "APRV_ID") == gdsUser.GetColumn(0, "USER_ID")) // 결재자인 경우
					btnstat = "00110";								
				else if (sDeptCd == gdsUser.GetColumn(0, "TEAM_CD") || sApro=="T") // 요청자와 같은 부서인  경우
					btnstat = "01000";	
				stStatNm.text = "승인요청";	
				break;
			case "003":// 반려
				if (objDS.GetColumn(nMaxStatRow, "UNSTOR_ID") == gdsUser.GetColumn(0, "USER_ID")) // 요청자인 경우
					btnstat = "10000";				
				stStatNm.text = "반려";	
				break;
			case "004":	
				if (objDS.GetColumn(nMaxStatRow, "APRV_ID") == gdsUser.GetColumn(0, "USER_ID")) // 결재자인 경우
					btnstat = "00001";								
				stStatNm.text = "결재완료";	
				break;
		}

		btnVibible(btnstat);		

		// 같은 부서인 경우 수정 가능. 직접 입력중인 경우
		if (objDS.GetColumn(nMaxStatRow, "COMM_NO") == gdsUser.GetColumn(0, "BRNC_CD") &&
		    sStatCd <= "001") {
		    bEditable = true;
		} else {
			bEditable = false;
		}
		//divBtnLst.btnSave.enable = bEditable;  //저장
	} else {
		btnVibible(btnStat);
	}
}

