function fcDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (obj.id == "dsGoodUnStor") {
		if (strColumnId == "RM_UNSTOR_CNT") {
			var UnstorCnt = 0;
			UnstorCnt = dsGoodUnStor.GetColumn(nRow, "UNSTOR_CNT_CUM") - 
						dsGoodUnStor.GetColumn(nRow, "DISUSE_CNT") - 
						dsGoodUnStor.GetColumn(nRow, "RM_UNSTOR_CNT");
			dsGoodUnStor.SetColumn(nRow, "UNSTOR_CNT", UnstorCnt);
		} else 	if (strColumnId == "DISUSE_CNT" ||strColumnId == "UNSTOR_CNT") {
			var RmUnstorCnt = 0;
			RmUnstorCnt = dsGoodUnStor.GetColumn(nRow, "UNSTOR_CNT_CUM") - 
						dsGoodUnStor.GetColumn(nRow, "DISUSE_CNT") - 
						dsGoodUnStor.GetColumn(nRow, "UNSTOR_CNT");
			dsGoodUnStor.SetColumn(nRow, "RM_UNSTOR_CNT", RmUnstorCnt);
		}
	} else if (obj.id == "dsYearItemOut") {
		if (strColumnId == "RM_UNSTOR_CNT") {
			var UnstorCnt = 0;
			UnstorCnt = dsYearItemOut.GetColumn(nRow, "BALANCE_CNT") - 
						dsYearItemOut.GetColumn(nRow, "DISUSE_CNT") - 
						dsYearItemOut.GetColumn(nRow, "RM_UNSTOR_CNT");
			dsYearItemOut.SetColumn(nRow, "UNSTOR_CNT", UnstorCnt);
		} else 	if (strColumnId == "DISUSE_CNT" ||strColumnId == "UNSTOR_CNT") {
			var RmUnstorCnt = 0;
			RmUnstorCnt = dsYearItemOut.GetColumn(nRow, "BALANCE_CNT") - 
						dsYearItemOut.GetColumn(nRow, "DISUSE_CNT") - 
						dsYearItemOut.GetColumn(nRow, "UNSTOR_CNT");
			dsYearItemOut.SetColumn(nRow, "RM_UNSTOR_CNT", RmUnstorCnt);
		}
	}
}

