/*-------------------------------------------------------------------------------------------------+
>>  쪽지나 문자메세지 전송처리
+-------------------------------------------------------------------------------------------------*/
function fcSendMessenger(){
	var sSendUserId   = gdsUser.GetColumn(0,"USER_ID");
	var sSendUserNm   = gdsUser.GetColumn(0,"USER_NM");
	var sSendUserHpNo = gdsUser.GetColumn(0,"HP_NO");
	var sMngr = "";

	var sTitle	  = "지정좌석실 상품재고 결재요청";
	var sDesc	  = "";
	var sLinkMenuId = "REM5040";

	var RecvID = "";
	var objDS;

	if (tab0.TabIndex == 0) objDS = dsGoodUnStor;
	else                    objDS = dsYearItemOut;

	// 001:등록, 002:결재요청, 003:관리자반려, 004부서승인		
	switch (slastStatCd) {
		case "001002":	//승인요청 : 지점장에게 쪽지 전송
		case "003002":	//승인요청 : 지점장에게 쪽지 전송
			sDesc = "상품재고 결재 요청 건이 있습니다.";
			RecvID = objDS.GetColumn(nMaxStatRow, "APRV_ID");
			break;
		case "002001":	//부서 반려 ; 등록자에게 쪽지 전송
		case "002003":	//부서 반려 ; 등록자에게 쪽지 전송
		case "004001":	//부서 반려 ; 등록자에게 쪽지 전송
			sDesc = "상품재고 결재 요청이 반려되었습니다.";
			RecvID = objDS.GetColumn(nMaxStatRow, "UNSTOR_ID");
			break;
		default:
			return;
	}
	// TEST
	//RecvID = "05102";

	slastStatCd = "";
	if (RecvID == "") return;
	fcd_SendMessage(gdsUser.GetColumn(0, "USER_ID")	// 송신자 사번 
					, RecvID	// 수신자 사번 
					, sTitle	    // 제목
					, sLinkMenuId 	// 호출할 메뉴ID
					, sDesc			// 내용
					, "comSendMsg"
					);

}

function fcTabEditable(bEitable1, bEitable2)
{
	tab0.tab1.grdGoodUnStor.Editable = bEitable1;
	tab0.tab2.grdYearItemOut.Editable = bEitable2;
	if (!bEitable1 && !bEitable2) {
		this.divBtnLst.btnSave.enable = false;
	}
}
