}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	fcInit(obj);
	return true;
}



/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";
	if ( !fcSearchCheck(obj) ) return;

	if (tab0.tabindex == 0) {
		var YrItemYn = dsCommNo.GetColumn(cboSearchBrncCd.Index, "CD_TERM3");
		if (Mid(YrItemYn,0,1) == "1") return ;	// 단가계약관리 지점에서 일반 품목 조회 불가

		sServiceName = "rem5040-service:searchGoodUnStorSign";
		sInDataSet   = "";
		sOutDataSet  = "dsGoodUnStor=dsGoodUnStor ";
	} else if (tab0.tabindex == 1) {
		sServiceName = "rem5040-service:searchYearItemOutSign";
		sInDataSet   = "";
		sOutDataSet  = "dsYearItemOut=dsYearItemOut ";
	} else {
		return;
	}

	 //조회조건 : 
	 fcd_AddParam("BRNC_CD", cboSearchBrncCd.Value);
 	 fcd_AddParam("UNSTOR_DT", cboStndYear.Value+cboStndMM.Value+"32"); //매월 32일 기준으로 데이터를 조회 
	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchGoodType(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	sServiceName = "rem5020-service:searchGoodType";
	sInDataSet   = "";
	sOutDataSet  = "dsGoodType=dsGoodType ";

	//조회조건 : 
	fcd_AddParam("BRNC_CD", cboSearchBrncCd.Value);
	fcd_AddParam("USE_YN",  "Y");

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


