function fcBtnOnClick(obj)
{
	var curStat = dsGoodUnStor.GetColumn(nMaxStatRow, "STAT_CD");
	if (tab0.TabIndex == 1) curStat = dsYearItemOut.GetColumn(nMaxStatRow, "STAT_CD");
	var sReqBrncCd = "";
	if (fnc_isnull(curStat)) curStat = "000";
	switch (obj.id) {
		case "btnReqAprv":		// 지점 승인요청
			fcSaveStat(curStat, "002");
			break;			
		case "btnWithdraw":		// 지점 결재요청 회수
			fcSaveStat(curStat, "001");
			break;			
		case "btnBrncAprv":		// 지점장 결재
			fcSaveStat(curStat, "004");
			break;			
		case "btnBrncReturn":	// 지점장 반려
			fcSaveStat(curStat, "001");
			break;			
		case "btnCnclAprv":	// 지점장 결재취소
			fcSaveStat(curStat, "001");
			break;			
	}
}

// 권한에 따른 버튼 제어
function SettingButton()
{
	var bEditable = false;
	var btnStat = "";	
	sStatCd = "000";
	var objDS;
	if (tab0.TabIndex == 0) objDS = dsGoodUnStor;
	else                    objDS = dsYearItemOut;
	if (objDS.RowCount() >= 0) {
		// 상태에 따른 버튼 제어
		for (var iCnt=0;iCnt<objDS.rowcount();iCnt++) {
			if (objDS.GetColumn(iCnt, "STAT_CD") > sStatCd)  {
				sStatCd = objDS.GetColumn(iCnt, "STAT_CD");
				nMaxStatRow = iCnt;
			}
		}	
		// 001:등록, 002:결재요청, 003:관리자반려, 004부서승인
		// 011:관리자 접수, 011:승인요청, 012:최종승인, 021:결과입력
		switch (sStatCd) {
			case "000":
				//btnstat = "00000";					// 저장
				//stStatNm.text = "";	
				//break;
			case "001":
			    if (objDS.GetColumn(nMaxStatRow, "APRV_ID") == gdsUser.GetColumn(0, "USER_ID")) // 결재자인 경우
					btnstat = "00000";								
				else if (sDeptCd == gdsUser.GetColumn(0, "TEAM_CD")  || sApro=="T") // 요청자와 같은 부서인  경우
					btnstat = "10000";
				stStatNm.text = "";	
				break;
			case "002":	// 지점 결재 요청
				if (objDS.GetColumn(nMaxStatRow, "APRV_ID") == gdsUser.GetColumn(0, "USER_ID")) // 결재자인 경우
					btnstat = "00110";								
				else if (sDeptCd == gdsUser.GetColumn(0, "TEAM_CD") || sApro=="T") // 요청자와 같은 부서인  경우
					btnstat = "01000";	
				stStatNm.text = "승인요청";	
				break;
			case "003":// 반려
				if (objDS.GetColumn(nMaxStatRow, "UNSTOR_ID") == gdsUser.GetColumn(0, "USER_ID")) // 요청자인 경우
					btnstat = "10000";				
				stStatNm.text = "반려";	
				break;
			case "004":	
				if (objDS.GetColumn(nMaxStatRow, "APRV_ID") == gdsUser.GetColumn(0, "USER_ID")) // 결재자인 경우
					btnstat = "00001";								
				stStatNm.text = "결재완료";	
				break;
		}
		btnVibible(btnstat);		

		// 같은 부서인 경우 수정 가능. 직접 입력중인 경우
		if (objDS.GetColumn(nMaxStatRow, "COMM_NO") == gdsUser.GetColumn(0, "BRNC_CD") &&
		    sStatCd <= "001") {
		    bEditable = true;
		} else {
			bEditable = false;
		}
		//divBtnLst.btnSave.enable = bEditable;  //저장
	} else {
		btnVibible(btnStat);
	}
}

function btnVibible(btnStat)
{
	var bdefault = "00000";	
	var arrBtn = array(5);
	arrBtn[0]  = divBtn.btnReqAprv;
	arrBtn[1]  = divBtn.btnWithdraw;
	arrBtn[2]  = divBtn.btnBrncReturn;	
	arrBtn[3]  = divBtn.btnBrncAprv;	
	arrBtn[4]  = divBtn.btnCnclAprv;	

	if (btnStat == "") btnStat = bdefault;

	var leftval = 0;
	for(var i=0 ; i < arrBtn.length(); i++ ) {
		arrBtn[i].Visible = iif(substr(btnstat,i,1) == "1", true, false);
		if (arrBtn[i].Visible) {
			arrBtn[i].left = leftval;
			leftval += arrBtn[i].width + 1;
		}
	}
	divBtn.left = 800 - leftval;
	divbtn.width = leftval;
}	


function fcDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (obj.id == "dsGoodUnStor") {
		if (strColumnId == "RM_UNSTOR_CNT") {
			var UnstorCnt = 0;
			UnstorCnt = dsGoodUnStor.GetColumn(nRow, "UNSTOR_CNT_CUM") - 
						dsGoodUnStor.GetColumn(nRow, "DISUSE_CNT") - 
						dsGoodUnStor.GetColumn(nRow, "RM_UNSTOR_CNT");
			dsGoodUnStor.SetColumn(nRow, "UNSTOR_CNT", UnstorCnt);
		} else 	if (strColumnId == "DISUSE_CNT" ||strColumnId == "UNSTOR_CNT") {
			var RmUnstorCnt = 0;
			RmUnstorCnt = dsGoodUnStor.GetColumn(nRow, "UNSTOR_CNT_CUM") - 
						dsGoodUnStor.GetColumn(nRow, "DISUSE_CNT") - 
						dsGoodUnStor.GetColumn(nRow, "UNSTOR_CNT");
			dsGoodUnStor.SetColumn(nRow, "RM_UNSTOR_CNT", RmUnstorCnt);
		}
	} else if (obj.id == "dsYearItemOut") {
		if (strColumnId == "RM_UNSTOR_CNT") {
			var UnstorCnt = 0;
			UnstorCnt = dsYearItemOut.GetColumn(nRow, "BALANCE_CNT") - 
						dsYearItemOut.GetColumn(nRow, "DISUSE_CNT") - 
						dsYearItemOut.GetColumn(nRow, "RM_UNSTOR_CNT");
			dsYearItemOut.SetColumn(nRow, "UNSTOR_CNT", UnstorCnt);
		} else 	if (strColumnId == "DISUSE_CNT" ||strColumnId == "UNSTOR_CNT") {
			var RmUnstorCnt = 0;
			RmUnstorCnt = dsYearItemOut.GetColumn(nRow, "BALANCE_CNT") - 
						dsYearItemOut.GetColumn(nRow, "DISUSE_CNT") - 
						dsYearItemOut.GetColumn(nRow, "UNSTOR_CNT");
			dsYearItemOut.SetColumn(nRow, "RM_UNSTOR_CNT", RmUnstorCnt);
		}
	}
}


/*-------------------------------------------------------------------------------------------------+
>>  결재요청/ 회수 등 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveStat(oldStatCd, newStatCd) {
	var objDS;
	if (tab0.tabindex == 0) objDS = dsGoodUnStor;
	else                    objDS = dsYearItemOut;

	var strMM = cboStndMM.Value;
	var strYYMM = cboStndYear.Value+cboStndMM.Value;
	if (!confirm(strMM + "월에 해당하는 모든 자료에 대해 결재 처리가 됩니다. \n계속 진행하시겠습니까?")) {
		return;
	}

	for (var iCnt=0;iCnt<objDS.RowCount();iCnt++) {
		if (objDS.GetColumn(iCnt, "UNSTOR_CNT") > 0)
			objDS.SetColumn(iCnt, "STAT_CD", newStatCd);
	}

	var sServiceName = "rem5040-service:saveUnStorStat";
	var sInDataSet   = "dsGoodUnStor=dsGoodUnStor:u ";
	var sOutDataSet  = "dsGoodUnStor=dsGoodUnStor ";
	if (tab0.TabIndex == 1) {
		sServiceName = "rem5040-service:saveYearItemOutStat";
		sInDataSet   = "dsYearItemOut=dsYearItemOut:u ";
		sOutDataSet  = "dsYearItemOut=dsYearItemOut ";
	}

	fcd_AddParam("TEAM_CD",     sDeptCd);
	fcd_AddParam("BRNC_CD",     cboSearchBrncCd.Value);
	fcd_AddParam("UNSTOR_DT",   strYYMM);
	fcd_AddParam("OLD_STAT_CD", oldStatCd);		//현재 상태
	fcd_AddParam("NEW_STAT_CD", newStatCd);		//변경할 상태

	sLastStatCd = oldStatCd+newStatCd;	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

