/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {

	if (tab0.TabIndex == 0) dsGoodHist.ClearData();
	else                    dsYearItemHist.ClearData();

}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}



/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";
	if ( !fcSearchCheck(obj) ) return;

	if (tab0.TabIndex == 0) {
		var YrItemYn = dsCommNo.GetColumn(cboSearchBrncCd.Index, "CD_TERM3");
		if (Mid(YrItemYn,0,1) == "1") return ;	// 단가계약관리 지점에서 일반 품목 조회 불가

		sServiceName = "rem5050-service:searchGoodHist";
		sInDataSet   = "";
		sOutDataSet  = "dsGoodHist=dsGoodHist ";
	} else  {
		fcSearchYearItem();

		sServiceName = "rem5050-service:searchYearItemHist";
		sInDataSet   = "";
		sOutDataSet  = "dsYearItemHist=dsYearItemHist ";
	}

	 //조회조건 : 
	 fcd_AddParam("BRNC_CD", cboSearchBrncCd.Value);
 	 fcd_AddParam("STR_DT", calStrDt.Value);
 	 fcd_AddParam("END_DT", calEndDt.Value);
	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchGoodType(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	sServiceName = "rem5020-service:searchGoodType";
	sInDataSet   = "";
	sOutDataSet  = "dsGoodType=dsGoodType ";

	//조회조건 : 
	fcd_AddParam("BRNC_CD", cboSearchBrncCd.Value);
	fcd_AddParam("USE_YN",  "Y");

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


/*-----------------------+
|  연간단가 품목 조회  |
+------------------------*/
function fcSearchYearItem(obj)
{
	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	 sServiceName = "rem5070-service:searchYearItem";
	 sInDataSet   = "";
	 sOutDataSet  = "dsYearItem=dsYearItem ";

	 //조회조건 : 
	 fcd_AddParam("BRNC_CD", cboSearchBrncCd.Value);
	 fcd_AddParam("STR_DT",  calStrDt.Value);
	 fcd_AddParam("END_DT",  calEndDt.Value);

	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}
