/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	if ( sServiceName = "common-service:searchCode" ) {			// 공통코드조회후 해당 지점의 상품분류 조회
		//연간단가계약품목 관리지점
		var YrItemYn = dsCommNo.GetColumn(cboSearchBrncCd.Index, "CD_TERM3");
		if (Mid(YrItemYn,0,1) == "1") {
			tab0.tabindex = 1;
			fcTabEditable(false, true); 
		} else {
			tab0.tabindex = 0;		
			fcTabEditable(true, false); 
		}
		fcSearchGoodType();
	} else if ( sServiceName = "rem5020-service:searchGoodType" ) {	// 상품분류 조회후 상품 내역 조회
		fcSearch();
	} else if ( sServiceName = "rem5050-service:searchGoodHist" ) {	// 상품분류 조회후 상품 내역 조회

	} else if ( sServiceName = "rem5050-service:searchYearItemHist" ) {	// 연간단가품목 조회
		//dsYearItemHist.GroupKey =	"YEAR_ITEM_SEQ_NO";		
	} else if (sServiceName = "rem5070-service:searchYearItem" ) {
		var nRow = dsYearItem.InsertRow(0);
		dsYearItem.SetColumn(nRow, "YEAR_ITEM_SEQ_NO", 0);
		dsYearItem.SetColumn(nRow, GOOD_NM, "전체");
		cboYearitem.Index = 0;
	}

}

function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{	
	switch ( obj.id ) {
		case "cboSearchBrncCd":		
			//연간단가계약품목 관리지점
			var YrItemYn = dsCommNo.GetColumn(cboSearchBrncCd.Index, "CD_TERM3");
			if (Mid(YrItemYn,0,1) == "1") {
				tab0.tabindex = 1;
				fcTabEditable(false, true); 
			} else {
			 	tab0.tabindex = 0;		
				fcTabEditable(true, false ); 
			}
			setReg("BRNC_CD", cboSearchBrncCd.Value);
			fcSearchGoodType();			 
			break;
	}
}

function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	switch ( obj.id ) {		
		case "dsBrncCd":
			dsCommNo.copy(dsBrncCd);
			if (sApro == "T") {
				dsCommNo.InsertRow(0);
				dsCommNo.SetColumn(0, "CD", "%");
				dsCommNo.SetColumn(0, "CD_NM", "전체");
			} else {
				//cboSearchBrncCd.Value = GdsUser.GetColumn(0, "BRNC_CD");
				dsCommNo.Filter("CD_TERM2='"+GdsUser.GetColumn(0, "TEAM_CD")+"'");	
			}
			//cboSearchBrncCd.index = 0;
			cboSearchBrncCd.Value = getReg("BRNC_CD");


		break;		
	}
}




