function fcTabEditable(bEitable1, bEitable2)
{
	tab0.tab1.Enable = bEitable1;
	tab0.tab2.Enable = bEitable2;

	stYearItem.Visible        = bEitable2;
	cboYearItem.Visible       = bEitable2;
	btnYearItemFilter.Visible = bEitable2;
}




function fcBtnOnClick(obj)
{
	if (obj.id == "btnYearItemFilter") {
		if (cboYearItem.Value != 0) {
			dsYearItemHist.Filter("YEAR_ITEM_SEQ_NO="+cboYearItem.Value);
		} else {
			dsYearItemHist.UnFilter();
		}
	}
}
